INSERT INTO `tour_trongnuoc` (`id`, `code`, `title`, `image`, `slug_tour`, `date_start`, `start`, `country_id`, `description`, `time`, `price`, `status`, `amount`, `hot`, `giamgia`, `count`) VALUES
(2, 'TB1', 'Du lịch Hà Giang khám phá mùa tam giác mạch nghe nhạc Acaustic giữa núi rừng Tây Bắc', 'images/tour-ha-giang-mua-hoa-tam-giac-mach.jpg', 'du-lich-ha-giang-kham-pha-mua-tam-giac-mach-nghe-nhac-acaustic-giua-nui-rung-tay-bac', 'Thứ 6 Hàng Tuần', 2, 13, '<p><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 01: HÀ NỘI – QUẢN BẠ - YÊN MINH - ĐỒNG VĂN&nbsp;</span></strong></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">05h30: Xe ô tô và hướng dẫn viên&nbsp;đón Quý khách tại điểm hẹn khởi hành đi tour du lịch Hà Giang - vùng đất có chè san, rượu mật ong và thắng cố, xứ sở của đào phai, hoa lê, truyền thống và náo nhiệt trong buổi chợ phiên…Quý khách dừng chân dùng bữa sáng tự túc tại đường rẽ đi Tam Đảo.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">12h00: Quý khách dùng bữa trưa tại thành phố Hà Giang. Sau bữa trưa quý khách khởi hành trình đi tham quan Cổng Trời Quản Bạ – Núi Đôi Cô Tiên quý khách có dịp chiêm ngưỡng Núi Đôi Cô Tiên - “tác phẩm nghệ thuật từ thiên nhiên” của tạo hoá ban tặng cho vùng đất Hà Giang, cùng nghe hướng dẫn viên kể về sự tích núi đôi cô tiên, vô vùng hấp dẫn và thú vị. Tiếp tục hành trình quý khách có cơ hội chinh phục và ngắm dốc Bắc Xum dài 7 km, từ trên trên đỉnh dốc quý khách có thể &nbsp;ngắm được toàn cảnh cung đường ngoằn nghèo của Dốc Bắc Xum vô cũng kỳ vỹ. Sau đó khởi hành đi Đồng Văn.</span></p>\r\n\r\n<p><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; color: rgb(35, 82, 124); text-decoration-line: none; outline: 0px; font-family: arial, helvetica, sans-serif; font-size: 14px; text-align: justify;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20Tay%20bac/quan-ba-viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:504px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">18h30: Tiếp tục hành trình đến với phố cổ Đồng Văn quý khách nhận phòng khách sạn và dùng bữa tối tại nhà hàng. Sau bữa tối quý khách cùng tham ra chương trình “Acoustic mountain music tour” tại trung tậm phố cổ Đồng Văn cùng nhâm nhi ly coffe, cùng hát cho nhau nghe những tình khúc lãng mạn giữa núi rừng. (Chương trình áp dụng cho đoàn trên 18 khách)</span></p>\r\n\r\n<p><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; color: rgb(35, 82, 124); text-decoration-line: none; outline: 0px; font-family: arial, helvetica, sans-serif; font-size: 14px; text-align: justify;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20Tay%20bac/ha-giang-viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:550px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 100, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 02: ĐỒNG VĂN – ĐÈO MÃ PHÍ LÈNG - CỘT CỜ LŨNG CÚ – DINH HỌ VƯƠNG&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">07h00: Sau bữa sáng quý khách làm thủ tục trả phòng khách sạn khởi đến thăm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Mã Pì Lèng (có âm đọc Mã Pí Lèng): là tên gọi của cung đường đèo hiểm trở thuộc Hà Giang, dài khoảng 20 km, nằm trên con đường Hạnh Phúc nối liền thành phố Hà Giang, huyện Đồng Văn và thị trấn Mèo Vạc. Mã Pì Lèng gọi theo tiếng Quan Hỏa chỉ “sống mũi con ngựa” theo nghĩa đen, nói rộng ra miêu tả sự hiểm trở của đỉnh núi, nơi dốc cao đến mức con ngựa đi qua phải tắt thở, hoặc đỉnh núi dựng đứng như sống mũi con ngựa. Con đường đèo uốn lượn qua những vách núi, vượt qua đỉnh Mã Pì Lèng ở độ cao 2000 mét, dưới chân là vực sâu sông Nho Quế như xẻ đôi một bên là đỉnh Mã Pì Lèng và một bên là Săm Pun (Sam Pun), nơi có cột mốc biên giới và cửa khẩu thông sang Điền Bồng, Trung Quốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; color: rgb(35, 82, 124); text-decoration-line: none; outline: 0px; font-family: arial, helvetica, sans-serif; font-size: 14px; text-align: justify;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20Tay%20bac/hoa-tam-giac-mach-ha-giang-viet-nam-travel.png\" style=\"border:0px; box-sizing:border-box; height:544px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">Cột cờ Lũng Cú: Được xây dựng trên đỉnh núi Rồng – một ngọn núi đá vôi tuổi Cambri hệ tầng Chang Pung với độ cao 1700m so với mực nước biển, cách huyện lỵ Đồng Văn 24km, cách thành phố Hà Giang 154km.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">Dinh thự của “Vua Mèo”: Tọa lạc ở thung lũng Sà Phìn (xã Sà Phìn, huyện Đồng Văn), cách thị xã Hà Giang 135km và cách thị trấn Đồng Văn 20km. Ngồi trên xe băng qua những cung đường ngoằn ngoèo uốn lượn theo vách núi, tôi mê mải ngắm vẻ đẹp hùng vĩ của cao nguyên đá mênh mông với những thửa ruộng bậc thang cheo leo, từ hốc đá tai mèo hoa rừng vẫn vươn lên khoe sắc đầy kiêu hãnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">12h00: Quý khách dùng bữa trưa tại nhà hàng tại Dinh Vua Mèo</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:14px\">Chiều: Sau bữa trưa đoàn tiếp tục khởi hành đi thăm thung lũng Sủng Là&nbsp;(làng văn hóa Lũng Cẩm) từng là nơi diễn ra bối cảnh của bộ phim “ Chuyện của Pao”, ngắm&nbsp;cánh đồng hoa Tam Giác Mạch trải dài bên đường và hai bên sườn núi. Sau đó đoàn trở về thị trấn Yên Minh nhận phòng khách sạn nghỉ ngơi. Quý khách dùng bữa tối tại nhà hàng nghỉ đêm tại khách sạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; color: rgb(35, 82, 124); text-decoration-line: none; outline: 0px; font-family: arial, helvetica, sans-serif; font-size: 14px; text-align: justify;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20Tay%20bac/mua-lua-chin_viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:656px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 100, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 03: YÊN MINH – QUẢN BẠ – HÀ NỘI&nbsp;&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">06h30: Sau bữa sáng quý khách làm thủ tục trả phòng. Sau đó lên xe đoàn trở về Hà Nội, trên đường đi ghé tham quan và chụp ảnh tại cột mốc km sô 0 và tượng đài Bác Hồ, quảng trường thành phố Hà Giang.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">11h30: Dùng bữa trưa tại nhà hàng cơm dân tộc thành phố Hà Giang.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">13h00: Đoàn lên xe trở về Hà Nôị, trên đường dừng chân mua đặc sản địa phương về làm quà.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">19h00: Về đến Hà Nội kết thúc tour du lịch Hà Giang 03 ngày 02 đêm.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<div class=\"w100 fl\" style=\"box-sizing: border-box; width: 815.359px; float: left; color: rgb(0, 0, 0); font-family: \">\r\n<div class=\"b-detail-ct-tour w100 fl top-20\" style=\"box-sizing: border-box; margin-top: 20px; width: 815.359px; float: left;\">\r\n<div class=\"tab-content\" style=\"box-sizing: border-box;\">\r\n<div class=\"tab-pane fade in active\" id=\"home\" style=\"box-sizing: border-box; opacity: 1; transition: opacity 0.15s linear 0s;\">\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(35, 82, 124); text-decoration-line: none; outline: 0px;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20Tay%20bac/viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:560px; max-width:100%; vertical-align:middle; width:840px\" /></a></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', 3, 2118000, 1, 15, 1, 10, 13),
(3, 'tb2', 'Du lịch Mù Cang Chải khám phá mùa lúa chín nghe nhạc Acaustic', 'images/e7f24ded88b59507418e189815d6ad16.jpg', 'du-lich-mu-cang-chai-kham-pha-mua-lua-chin-nghe-nhac-acaustic', 'Thứ 7 Hàng Tuần', 2, 13, '<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 01: HÀ NỘI - THANH SƠN – NGHĨA LỘ - TÚ LỆ - BẢN LƯỚT – MÙ CANG CHẢI&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">05h00: Xe ô tô và hướng dẫn viên của Vietnam Travel&nbsp;đón quý khách tại nhà hát lớn Hà Nội. Khởi hành đi Mù Cang Chải theo quốc lộ 32. Đoàn dừng chân nghỉ ngơi dùng bữa sáng tự túc tại Sơn Tây. Sau bữa sáng quý khách tiếp tục khởi hành đi tour Mù Cang Chải.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">09h00: Quý khách dừng chân nghỉ ngơi ngắm cảnh bàn ngàn đồi trè xanh mướt Thanh Sơn – Phú Thọ. Nơi đã đi vào thơ ca của nhà thơ Tố Hữu.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\">\"Đẹp vô cùng, Tổ quốc ta ơi!</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\">Rừng cọ đồi chè, đồng xanh ngào ngạt</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\">Nắng chói sông Lô, hò ô tiếng hát</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\">Chuyến phà dào dạt bến nước Bình Ca...\"</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">12h00: Tiếp tục hành trình đến với Mường Lò nơi nổi tiếng với những đặc sản của người dân tộc Thái như: Rêu suối, bánh trưng đen, nộm hoa ban, xôi nếp Tú Lệ….. quý khách dừng chân dùng bữa trưa và cùng tìm hiểu văn hóa ẩm thực địa phương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">13h00: Sau bữa trưa quý khách tiếp tục hành trình đến đèo Khau Phạ một trong những tứ đại đình đèo miền bắc theo tiếng của người dân tộc Thái có nghĩa là Sừng Trời nơi giao hòa giữa trời và đất, quý khách dừng chân ngắm cảnh, chụp ảnh tại đèo Khau Phạ - đèo dài nhất trên quốc lộ 32 với độ dài trên 32km và độ cao là 2100m, cùng ngắm bản Lìm Mông, Lìm Thái từ trên cao vào mùa lúa chín……</span><span style=\"font-family:arial,helvetica,sans-serif\">Tiếp tục hành trình đến với bản Lướt – Ngọc Chiến – Sơn La quý khách tự do trọn cho mình một phòng tắm riêng hoặc tắm tập thể tại suối khoáng nóng bản Lướt. Cùng đắm mình trong làn nước suối ấm áp xua tan mệt mỏi sau những ngày làm việc căng thẳng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">17h00: Quý khách lên xe khởi hành về home stay tại Mù Cang Chải dùng bữa tối với những món ăn mang hương vị núi rừng Tây Bắc do chính gia chủ là người dân tộc Thái chế biến phục vụ quý khách như xôi nếp nương, cá nướng Pa Phỉnh Tộp……….</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Sau bữa tối quý khách cùng tham ra chương trình “Acoustic tour” tại bản Thái Mù Cang Chải cùng nhâm nhi ly coffe, cùng hát cho nhau nghe những tình khúc giữa núi rừng Mù Cang Chải (Áp dụng cho đoàn trên 20 khách). Quý khách nghỉ đêm tại home stay tại bản Thái (cách trung tâm thị trấn Mù Cang Chải khoảng 800m).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 02: MÙ CANG CHẢI – CHẾ CU NHA – LA PÁN TẨN – TÚ LỆ - HÀ NỘI&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">07h00: Sau bữa sáng tiếp tục hành trình nơi quý khách không thể bỏ qua khi đến Mù Cang Chải là “Mâm Xôi”, Sân Mây cùng những cánh đồng ruộng bậc thang xã La Pán Tẩn, Chế Cu Nha vàng óng một góc trời. Quý khách sẽ có cơ hội chiêm ngưỡng một thiên đường ruộng bậc thang với nắng vàng và biển lúa với cảnh sắc làm say đắm lòng người cùng chụp cho mình những bức ảnh tuyệt đẹp bên bạn bè. Nơi đã được được công nhận là di sản phi vật thể của quốc gia.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">12h00: Quý khách dùng bữa trưa tại Tú Lệ với đặc sản cá suối và xôi nếp Tú Lệ nổi tiếng. Sau bữa trưa quý khách lên đường về Hà Nội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">18h00: Về đến Hà Nội kết thúc chương trình tour Mù Cang Chải – bản Lướt – Ngọc Chiến – Sơn La 2 ngày 1 đêm chia tay quý khách hẹn gặp quý khách trong những hành trình tiếp theo.</span></p>', 3, 1850000, 1, 30, 1, 0, 3),
(4, 'dn1', 'Du lịch Đà Nẵng - Hội an - Quy Nhơn - Đà nẵng 4 ngày 3 đêm Tour tiêu chuẩn kích cầu du lịch', 'images/tfd_201013121134_082121.jpg', 'du-lich-da-nang-hoi-an-quy-nhon-da-nang-4-ngay-3-dem-tour-tieu-chuan-kich-cau-du-lich', 'Hàng ngày', 2, 9, '<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-family:tahoma,geneva,sans-serif\">NGÀY 01: HUẾ - ĐÀ NẴNG - NGŨ HÀNH SƠN</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sau khi đón quý khách, chúng tôi sẽ làm thủ tục cho quý khách bay đến đến Đà Nẵng, sau khi đên Đà Nẵng, đoàn tham quan Ngũ Hành Sơn với Động Tàng Chơn, Động Hoa Nghiêm, Chùa Non Nước, Làng Đá Mỹ Nghệ. Về thành phố Đà Nẵng, đoàn viếng chùa Linh Ứng với tượng Phật Quan Thế Âm cao nhất Việt Nam (67m). Quý khách sẽ được chiêm ngưỡng toàn cảnh thành phố, núi rừng và biển đảo Sơn Trà một cách hoàn hảo nhất.Buổi tối, Quý khách tự do dạo phố biển hoặc lên thuyền khám phá sông Hàn về đêm. Nghỉ đêm tại Đà Nẵng.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; color: rgb(0, 0, 0); font-family: \">&nbsp;</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-family:tahoma,geneva,sans-serif\">NGÀY 02:&nbsp;ĐÀ NẴNG - HỘI AN - RỪNG DỪA BẢY MẪU</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sau khi ăn sáng, đoàn khởi hành đến Cẩm Thanh (Quảng Nam) tham quan:<br />\r\n- Rừng Dừa Bảy Mẫu: trên những chiếc thuyền thúng len lỏi vào những con kênh nhỏ trong rừng dừa, tìm hiểu về rừng dừa Bảy Mẫu. Quý khách được tận mắt xem người dân địa phương quăng chài, thả lưới trên sông để đánh bắt cá và có thể tự tay trải nghiệm kỹ năng quăng chài lưới của mình. Sau đó, thưởng thức màn lắc thúng chém nước điêu luyện của nghệ nhân.<br />\r\nSau khi ăn trưa đoàn về nhận phòng khách sạn nghỉ ngơi. Buổi chiều, đoàn tham quan:<br />\r\n- Phố Cổ Hội An:&nbsp; trải nghiệm xe đạp trợ lực thân thiện với môi trường tham quan Chùa Cầu; Nhà cổ Phùng Hưng; Tiệm Mót - Thưởng thức trà thảo mộc; Faifo Coffee – một trong những quán café đẹp&nbsp; ngắm toàn cảnh phố cổ; Hội Quán Phước Kiến… (chi phí nước uống tự túc)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Buổi tối, Quý khách ăn tối và tự do dạo phố.&nbsp;Nghỉ đêm tại Hội An.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; color: rgb(0, 0, 0); font-family: \">&nbsp;</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><span style=\"color:rgb(255, 0, 0)\"><strong>NGÀY 03: HỘI AN - QUẢNG NGÃI - QUY NHƠN - KHU DU LỊCH&nbsp;GỀNH RÁNG TIÊN SA</strong></span><br />\r\nOption tour: Trải nghiệm xe cổ ngắm bình minh trên biển tại Hội An<br />\r\n05h00 - 06h00 Quý khách có thể trải nghiệm xe điện cổ băng qua những tuyến phố thanh bình, đầy phong vị xưa cũ trong buổi sớm mai và ngắm nhìn ánh bình minh đang dần ló dạng tại biển Cửa Đại hoặc An Bàng (chi phí tự túc - Dịch vụ vui lòng book trước cho nhân viên kinh doanh trước 24 giờ).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sau khi ăn sáng, khởi hành về Quy Nhơn trên đường dừng tại Quảng Ngãi ăn trưa và ngắm Biển Sa Huỳnh. Tiếp tục khởi hành đến Quy Nhơn, tham quan:<br />\r\n- Kỳ Co: điểm du lịch đang được nhiều du khách quan tâm. Với màu nước trong xanh, bãi cát trắng dài và mịn, những cơn sóng biển êm ái cùng những tiểu cảnh check in đẹp… đã thu hút nhiều du khách khi đến Quy Nhơn. Ngoài tắm biển và thư giãn, Quý khách có thể chụp hình check in nhiều góc cực đẹp.&nbsp;<br />\r\n- Eo Gió: từ trên cao nhìn xuống, Eo Gió như một bức tranh sơn thủy hữu tình với trời, mây, nước… biển núi ôm nhau vừa gợi cảm, vừa hoang sơ đầy quyến rũ.<br />\r\nSau khi ăn tối xe đưa đoàn mua sắm đặc sản tại shop Phương Nghi như Rượu Bàu Đá, Nem Chợ Huyện, Bánh Hồng, Tré Bình Định, Bánh Ít Lá Gai…&nbsp;<br />\r\nNghỉ đêm tại Thành phố Quy Nhơn. Tối quý khách có thể tự do dạo phố biển, thưởng thức hải sản hoặc check in các quán café đẹp dọc biển&nbsp; như S-Blue hoặc Surf&nbsp; Bar…</span></p>\r\n\r\n<div style=\"box-sizing: border-box; color: rgb(0, 0, 0); font-family: \">&nbsp;</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-family:tahoma,geneva,sans-serif\">&nbsp;NGÀY 04:&nbsp;QUY NHƠN – ĐÀ NẴNG</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sau khi ăn sáng, xe đưa đoàn tham quan Tháp Đôi - Cụm tháp ấn tượng ngay trong lòng thành phố biển Quy Nhơn, có niên đại từ thế kỷ XII, mang nét kiến trúc tháp Champa Bình Định, vừa mang phong cách Khmer thời Angkorwat - Bayon. Khởi hành về Đà nẵng, Quý khách trở về điểm đón ban đầu, kết thúc hành trình du lịch. Hẹn gặp lại quý khách trong những chuyến du lịch sau!</span></p>', 4, 4900000, 1, 10, 1, 10, 9),
(5, 'dn2', 'Du lịch Đà Nẵng – Bà Nà – Cù Lao Chàm Tour tiêu chuẩn Từ TP. Hồ Chí Minh giá khuyến mãi', 'images/thong-tin-du-lich-da-nang9.jpg', 'du-lich-da-nang-ba-na-cu-lao-cham-tour-tieu-chuan-tu-tp-ho-chi-minh-gia-khuyen-mai', 'Hàng ngày', 1, 9, '<p style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 1: TP.hỒ cHÍ MINH – ĐÀ NẴNG – BÁN ĐẢO SƠN TRÀ</span></strong></span></p>\r\n\r\n<div style=\"box-sizing: border-box; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng:&nbsp;Quý khách có mặt tại ga quốc nội, sân bay trước giờ bay ít nhất hai tiếng.</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Đại diện công ty Vietnam Travel đón và hỗ trợ Quý Khách làm thủ tục đón chuyến bay đi&nbsp;Đà Nẵng. đến Đà nẵng, xe đưa quý khách về thành phố.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi &nbsp;trưa:&nbsp; Dùng bữa trưa tại nhà hàng với&nbsp;đặc sản nổi tiếng Đà Nẵng&nbsp;như:&nbsp;“Bánh tráng thịt heo 2 đầu da &amp; Mỳ Quảng”.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi chiều:&nbsp; Đoàn khởi hành tham quan:&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Bán Đảo Sơn Trà&nbsp;(Monkey Mountain) quay một vòng quanh Bán Đảo để mục kích toàn cảnh phố biển Đà Nẵng trên cao.&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Viếng&nbsp;Linh Ứng Tự&nbsp;– nơi có&nbsp;tượng Phật Bà 65m cao nhất Việt Nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Tắm biển Mỹ Khê&nbsp;– Đà Nẵng –&nbsp;1 trong 6 bãi biển quyến rũ nhất hành tinh (do tạp chí Fober – Mỹ bình chọn)&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Dùng cơm chiều tại nhà hàng.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi &nbsp;tối: Tham quan Công viên Châu Á – Asia Park Đà Nẵng, bên bờ Tây sông Hàn, sự kết hợp những nét độc đáo, mới lạ của các mô hình giải trí trên thế giới với những nét văn hóa đặc sắc đậm chất Á Đông (chi phí tự túc).&nbsp;&nbsp;Nghỉ đêm tại Đà Nẵng Khách sạn 3*.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/Linh-ung-tu_viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:556px; max-width:100%; vertical-align:middle; width:1000px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 2: CÙ LAO CHÀM – PHỐ CỔ HỘI AN</span></strong></span></p>\r\n\r\n<div style=\"box-sizing: border-box; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng:&nbsp;Sau bữa sáng ở khách sạn quý khách khởi hành tham quan:&nbsp;Cù Lao Chàm ( chưa bao vé Cù Lao)– Khu Dự trữ Sinh quyển Thế giới, một địa danh du lịch biển nổi tiếng của miền Trung. Đến đây Quý khách sẽ có dip khám phá một vùng thiên nhiên hoang sơ, thơ mộng hòa mình vào cuộc sống dân dã trong sự đón tiếp nồng hậu của cư dân làng chai; …Đến bến tàu cảng&nbsp;Cửa Đại&nbsp;đoàn lên thuyền cao tốc để viếng thăm đảo Cù Lao Chàm Quý khách có dịp tham quan:</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Chùa cổ Hải Tạng&nbsp;hơn 400 trăm năm tuổi, thăm “ Âu thuyền” nơi lánh bão của các thuyền đánh cá khi gặp thời tiết xấu và tìm hiểu đời sống của cư dân chai lưới sống trên đảo tại bãi Làng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Khu du lịch Bãi Bấc, Quý khách thay đồ tắm biển và lặn ngắm san hô (Quý khách lặn nổi ngắm san hô bằng mặt nạ và ông thở nhựa).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi &nbsp;trưa:&nbsp;Ăn trưa nhà hàng&nbsp;Cù Lao Chàm&nbsp;.&nbsp;Quý khách có dịp thưởng thức các loại đặc sản đậm đà hương vị biển Cù Lao Chàm như: ốc vú nàng, cua đá, tôm, mực ống…</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi chiều:&nbsp;Quý khách tự do tắm&nbsp;Biển Cù Lao Chàm, đắm mình trong làn nước xanh mát với bãi cát trắng và khí hậu trong lành.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Tiếp tục thăm khu bảo tồn biển và tham gia các hoạt động&nbsp;lặn biển (Snokelling)&nbsp;khám phá san hô…</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Về lại đất liền khởi hành qua&nbsp;Phố Cổ&nbsp;Hội An&nbsp;bách bộ tham quan và mua sắm Phố Cổ với: Chùa Cầu Nhật Bản, Nhà Cổ hàng trăm năm tuổi, Hội Quán Phước Kiến &amp; Xưởng thủ công mỹ nghệ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Trở về thành phố Đà Nẵng tham quan&nbsp;Ngũ Hành Sơn&nbsp;– được ví như hòn non bộ khổng lồ giữa lòng thành phố Đà Nẵng với Động Huyền Không, Chùa Linh Ứng, Chùa Tam Thai, Vọng Hải Đài, … mua sắm quà lưu niệm tại làng nghề điêu khắc đá Non Nước.&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Dùng cơm chiều tại nhà hàng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi &nbsp;tối: Quý khách tự do thưởng ngoạn&nbsp;du thuyền trên sông Hàn&nbsp;ngắm cảnh&nbsp;Đà Thành&nbsp;về đêm, hoặc tản bộ thưởng thức không khí trong lành bên&nbsp; bờ&nbsp;&nbsp;Hàn Giang với tượng Cá Chép Hóa Long&nbsp;– Biểu tượng mong muốn vươn lên của người Đà Nẵng. &nbsp;Nghỉ đêm tại Đà Nẵng Khách sạn 3*.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/Cham-Island-Tour.jpg\" style=\"border:0px; box-sizing:border-box; height:500px; max-width:100%; vertical-align:middle; width:1000px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 3: BÀ NÀ NÚI CHÚA – TẮM BIỂN MỸ KHÊ- TP. HỒ CHÍ MINH</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng:&nbsp;Dùng buffet sáng tại khách sạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Khởi hành đến với cao nguyên&nbsp;Bà Nà ( chưa bao vé cáp treo)&nbsp;nơi có khí hậu Châu Âu độc đáo và nổi tiếng với tuyến cáp treo kỷ lục mới của thế giới – Ngắm toàn cảnh thành phố Đà Nẵng từ trên cáp treo&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Lên Cao Nguyên Bana Hills Quý khách tự do tham quan, khu vui chơi giải trí tại Bà Nà:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Công Viên Fantasy&nbsp;với các trò chơi phiêu lưu mới lạ như: Vòng Quay Tình Yêu, Phi Công Skiver, Đường Đua Lửa, Ngôi Nhà Ma.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Rạp chiếu phim 3D Mega 360 độ&nbsp;với công nghệ hiện đại nhất và duy nhất có ở Viêt Nam, Hai rạp chiếu phim 4D và 5Di, Xe Trượt Ống, Hầm rượu, Vườn hoa tình yêu,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Bảo Tàng Sáp&nbsp;trưng bày hơn 40 tượng sáp những nhân vật nổi tiếng trên thế giới…</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Du khách đi chùa cầu bình an và sức khỏe cho gia đình:&nbsp;Chùa Linh Ứng, Đền Lĩnh Chúa Linh Từ, Tháp Linh Phong Tự, Tượng Thích Ca Mâu Ni, Lầu Chuông, Nhà Bia, Miếu Bà, Trú Vũ Trà Quán.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi trưa:&nbsp;Ăn trưa nhà hàng tại chân núi Bà Nà.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi chiều: Khởi hành về lại trung tâm thành phố. Hướng dẫn viên tiễn đoàn ra sân bay Đà Nẵng, đón chuyến bay về TP.Hồ Chí Minh. Kết thúc chương trình tham quan Đà Nẵng – Bà Nà – Cù Lao Chàm, chia tay và hẹn gặp lại Quý khách.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/du-lich-da-nang2.jpg\" style=\"border:0px; box-sizing:border-box; height:707px; max-width:100%; vertical-align:middle; width:1000px\" /></span></p>', 3, 4500000, 1, 25, 0, NULL, 0),
(6, 'pq1', 'Du lịch Phú Quốc khám phá đảo ngọc và nghỉ dưỡng hè 2022 (Tour trọn gói không phát sinh chi phí)', 'images/bai-sao-phu-quoc.png', 'du-lich-phu-quoc-kham-pha-dao-ngoc-va-nghi-duong-he-2022-tour-tron-goi-khong-phat-sinh-chi-phi', 'Hàng ngày', 1, 10, '<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 01: PHÚ QUỐC - ĐÔNG ĐẢO (ĂN TRƯA, TỐI)</strong></span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><strong><span style=\"color:rgb(0, 128, 0)\">Buổi Sáng:</span>&nbsp;</strong>Quý khách có mặt tại sân bay, hdv đón làm thủ tục đáp chuyến bay khởi hành đến Phú Quốc.&nbsp;</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\">Đến sân bay Phú Quốc, xe và HDV đón đoàn nghỉ ngơi, ghép đoàn và ăn trưa.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi Chiều:</strong></span>&nbsp;Đoàn khởi hành phía Đông Đảo:&nbsp;</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\">Quý khách<span style=\"color:rgb(0, 128, 0)\">&nbsp;<strong>tham quan Cơ sở ủ rượu vang Sim</strong></span>&nbsp;– Một loại rượu đặc sản tại địa phương, thưởng thức rượu Sim rừng miễn phí.</span></li>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Tham quan vườn tiêu</strong></span>&nbsp;- Với những nọc tiêu thẳng tắp, xanh mơn mỡn, nổi tiếng chắc hạt, thơm ngon.</span></li>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Tham quan trại nuôi ong mật</strong></span>&nbsp;- Tìm hiểu cuộc sống cần cù của những chú ong bé nhỏ và học cách lấy mật ong (quay mật) của người dân. Đến mùa trái cây, Quý khách còn được chiêm ngưỡng và tận tay hái các loại sầu riêng, chôm chôm ….trĩu quả trên cành.&nbsp;</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi Tối:</strong></span>&nbsp;Đoàn cơm tối, thưởng thức hải sản tươi ngon. Sau đó Du khách&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>tự do khám phá Phú Quốc về đêm, chợ đêm Dinh Cậu</strong></span>. Nghỉ đêm tại Phú Quốc.</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/lang-chai-ham-ninh.png\" style=\"border:0px; box-sizing:border-box; height:533px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 02: CÂU CÁ – LẶN NGẮM SAN HÔ – CÁP TREO HÒN THƠM (ĂN SÁNG, TRƯA, TỐI)</strong></span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi sáng</strong>:</span>&nbsp;Sau khi dùng điểm tâm sáng, xe đưa đoàn tiến về phía Nam, lên tàu câu cá.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Ngọc trai cao cấp Phú Quốc</strong>:</span>&nbsp;quý khách được trực tiếp tham quan quy trình nuôi trai lấy ngọc tại các cơ sở nuôi cấy ngọc trai chuyên nghiệp tại Phú Quốc.</span></li>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Sau đó quý khách lên trên tàu:</strong></span>&nbsp;Quý khách sẽ tận hưởng từng khoảnh khắc đáng nhớ khi chính tay mình buông câu bắt được những chú cá bồng mú phàm ăn trong các rạn san hô tại hòn Dừa, hòn Rỏi…</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Quý Khách có thể lựa chọn thêm chương trình:</strong>&nbsp;</span>Dịch vụ “ Đi bộ dưới đáy biển” tại tàu Namaste.&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">(Chi phí tự túc)</span>.</strong>&nbsp; Kết hợp lúc đi câu cá lặn ngắm san hô .</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Địa điểm hoạt động:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\">Tại Công Viên San Hô ở Hòn Rỏi , Thị trấn An Thới, Phú Quốc. Mùa hoạt động dự kiến từ 1 tháng 6 đến 30 tháng 9 hàng năm.</span></li>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\">Tại Công Viên San Hô ở Mũi Kỳ Lân, Hòn Thơm, Thị trấn An Thới, Phú Quốc. Mùa hoạt động dự kiến từ 13 tháng 10 đến 25 tháng 5 hàng năm.Khung giờ hoạt động trên tàu Namaste: Từ 8h đến 17h hàng ngày (theo giá quy định)</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi trưa:</strong></span>&nbsp;Ăn trưa trên tàu. Sau đó tắm biển, ngắm san hô. Sau đó quý khách tiếp tục dừng chân tại một trong những bãi biển đẹp nhất Phú Quốc với bãi cát dài thoang thoảng, trắng mịn. Đoàn tự do chụp hình lưu niệm.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Nhà tù Phú Quốc</strong></span>: khu di tích lịch sử - nơi chứng kiến bao tội ác của thực dân Pháp và đế quốc Mỹ khi giam cầm hơn 32 ngàn tù binh chiến tranh (có lúc lên&nbsp;&nbsp; đến con số 40 ngàn tù binh) trong khoảng thời gian tồn tại chưa đầy 6 năm.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\">Quy khách có thể chọn tự túc đi cáp treo Hòn Thơm khám phá 1 Đảo Ngọc từ trên&nbsp; cao qua tuyến cáp treo trên biển dài nhất thế giới (Vé tự túc vé ) Trở về khách sạn.Tắm biển hoặc hồ bơi.Tự do thư giản</span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi tối:</strong></span>&nbsp;Xe đưa đoàn dùng cơm tối tại nhà hàng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Quý khách tự do dạo chợ đêm Phú Quốc .</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/bai-sao-phu-quoc.png\" style=\"border:0px; box-sizing:border-box; height:564px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 03:&nbsp;</strong><strong>KHÁM PHÁ BẮC ĐẢO – KẾT HỢP VINPEARLLAND – SAFARI (ĂN SÁNG, TRƯA)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi sáng:</strong>&nbsp;</span>Qúy khách dùng điểm tâm. Xe và HDV đón qúy khách bắt đầu khởi hành tham quan những điểm du lịch về hướng Bắc đảo để cảm nhận vùng đất nhiều huyền bí:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Viếng đình thần Nguyễn Trung Trực</strong></span>&nbsp;– ngôi đền linh thiên với những giai thoại hào hùng về người anh hùng dân tộc của tổ quốc. Đến với&nbsp; ngôi đền là dịp để quý khách gửi gấm những tâm tư nguyện vọng cũng như cầu mong điều tốt lành cho gia đình và người thân của mình. Đây được xem là địa điểm không thể bỏ qua khi đến Phú Quốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Tham quan Gành Dầu ngắm biên giới hải đảo Việt Nam – Campuchia</strong>:</span>&nbsp;đến đây quý khách sẽ được tận hưởng vẻ đẹp thuần khiết của bãi Dài, bãi biển được xem là hoang sơ nhất Phú Quốc. Ngắm nhìn nơi xa xa hòn đảo của đất nước láng giềng Campuchia.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Khám Phá&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Phú Quốc UnitedCenter - Grandword&nbsp;</strong></span>,Trung tâm mới sầm uất và đa tiện tích của Phú Quốc với hệ sinh thái dịch vụ trọn vẹn từ dân sinh đến giải trí, nay được hoàn thiện ngoạn mục bằng sự ra mắt của Grand World, Thiên đường giải trí - mua sắm - lễ hội độc đáo và đa dạng bậc nhất Đông Nam Á với chuỗi trải nghiệm xuyên suốt 24h.</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/grand-wold-phu-quoc-viet-nam-travel.png\" style=\"border:0px; box-sizing:border-box; height:557px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Quy Khách được Checkin sống ảo Miễn phí tại đây bao gồm:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Siêu quần thể nghỉ dưỡng, khám phá, mua sắm, giả trí không ngủ hàng đầu khu vực Đông Nam Á (Phú Quốc United center)</strong></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi trưa:</strong></span>&nbsp;Qúy khách dùng&nbsp;cơm trưa&nbsp;tại Gành Dầu, cảm nhận gió biển và thưởng thức đặc sản biển.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Buổi chiều:</strong></span>&nbsp;&nbsp;Quý khách về lại khách sạn và resort – tắm hồ bơi và bãi biễn nghỉ ngơi.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Hoặc lựa chọn chương trình Vinpearlland</strong>:&nbsp;</span>Xe của khu Vinpearlland đưa quý khách đến khu vui chơi&nbsp;Vinpearlland&nbsp;<span style=\"color:rgb(0, 128, 0)\">(<strong>Chi phí tự túc</strong>)</span>, là&nbsp;một trong những trung tâm vui chơi giải trí lớn và hiện đại nhất Phú Quốc. Đến đây, qúy khách sẽ được tham gia nhiều trò chơi thú vị như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><strong>Vườn Thú Safari Phú Quốc: Khu vườn thú lớn thứ 2 Đông Nam Á</strong></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><strong>Vinpearlland</strong></span>,&nbsp;một trong những trung tâm vui chơi giải trí lớn và hiện đại nhất Phú Quốc. Đến đây, qúy khách sẽ được tham gia nhiều trò chơi thú vị như:</li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Trò chơi cảm giác mạnh:</strong></span>&nbsp;Tàu lượn siêu tốc, đu quay lật cảm giác mạnh, đu quay dây văng, đĩa quay siêu tốc, đĩa bay, đường trượt lốc xoáy..</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Khu vui chơi trong nhàCông viên nước</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Thuỷ cung Vinpearl:</strong></span>&nbsp;khám phá thế giới đại dương với nhiều loài sinh vật độc đáo như cá mập, cá hải tượng, Cua nhện, Tôm bác sĩ…Cùng hoà mình với nàng tiên cá khám phá dưới lòng đại dương và lạc vào thế giới cổ tích với nàng tiên cá.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Rạp chiếu phim 5D</strong>,&nbsp;<strong>Sân khấu nhạc nước</strong>:&nbsp;</span>tạo nên màu sắc huyền ảo li kì và hấp dẫn.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Xe của khu Vinpearlland sẽ đưa Quý Khách về lại trung tâm thị trấn Dương Đông.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Ăn tối tại nhà hàng .Tự do nghỉ ngơi hoặc đi chợ đêm .</span>&nbsp;</p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/du-lich-phu-quoc_viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:500px; max-width:100%; vertical-align:middle; width:1100px\" /></strong></span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 04: PHÚ QUỐC - HẸN GẶP LẠI (ĂN SÁNG)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><span style=\"color:rgb(0, 128, 0)\">Buổi sáng:</span>&nbsp;</strong>Qúy khách dùng điểm tâm sáng. Qúy khách tự do tham quan&nbsp;<strong>chợ Dương Đông</strong>, là trung tâm trao đổi, vận chuyển hàng hóa chính của đảo, có lịch sử phát triển lâu đời và lớn nhất Phú Quốc. Qúy khách tự do mua sắm đặc sản về làm quà cho gia đình, người thân.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Qúy khách tự do tắm biển, nghỉ ngơi tại khách sạn , đoàn làm thủ tục trả phòng khách sạn</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Đến giờ, đoàn lên xe đi sân bay Dương Đông, đáp chuyến bay trở về, kết thúc tour du lịch Phú Quốc, hẹn quý khách trong những chuyến du lịch tiếp theo cùg VietnamTravel.</span></p>\r\n\r\n<div>&nbsp;</div>', 4, 5900000, 1, 10, 0, NULL, 3);
INSERT INTO `tour_trongnuoc` (`id`, `code`, `title`, `image`, `slug_tour`, `date_start`, `start`, `country_id`, `description`, `time`, `price`, `status`, `amount`, `hot`, `giamgia`, `count`) VALUES
(7, 'tl5', 'Du lịch Thái Lan Khám phá Bangkok và Pattaya Từ HN Tour tiêu chuẩn 2022', 'images/bc7c37eceb4474ceae6688f802b7d55b.jpg', 'du-lich-thai-lan-kham-pha-bangkok-va-pattaya-tu-hn-tour-tieu-chuan-2022', 'Hàng ngày', 1, 1, '<p style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><u>Điểm đặc sắc của chuyến tham quan:</u></strong></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Tham quan công &nbsp;viên Siracha Tiger Zoo lớn nhất tại Thái Lan</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Đi tàu cao tốc và tắm nước biển trong xanh tại đảo San Hô – Coral Island</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Tham quan công viên thủy cung nổi tiếng của Pattaya – Trải ngiệm cảm giác đi bộ giữa làn nước trong xanh</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Tham quan trung tâm trang sức, vàng bạc đá quý, siêu thị mỹ phẩm World Gem Gallery</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Thưởng thức show diễn Bede hoành tráng nhất Thái Lan tại sân khấu mô phỏng đấu trường Roma Italia</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Tham quan mua sắm tại trung tâm đặc sản 3 miền Vườn Saithip</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Mua sắm thỏa thích tại siêu thị chuyên các loại đồ da mẫu mã độc đáo nhất</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Du thuyền trên dòng sông mẹ nhìn ngắm thành phố Bangkok đôi bờ</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Lễ phật bốn mặt linh thiêng tại đền thờ Erawan Bangkok</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Chiêm bái chùa thiêng Xá Lợi Phật Wat Yannawa</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Tham quan trấn quốc chi bảo Thái Lan Wat Traimit</strong></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Thỏa thích mua sắm tại trung tâm thương mại Patunam, chợ sỉ Platium, Central World, Big&nbsp; C…</strong></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><u>Quà tặng</u></strong><strong>:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 128, 128)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Mũ du lịch,vỏ hộ chiếu,nước uống trên xe.</strong></span></span></li>\r\n</ul>\r\n\r\n<hr />\r\n<h1 style=\"text-align:center\"><span style=\"font-size:20px\"><strong><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(255, 0, 0)\"><big>NỘI DUNG CHI TIẾT CHƯƠNG TRÌNH&nbsp;</big></span></span></strong></span></h1>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 01: HÀ NỘI&nbsp; – BANGKOK (ĂN TỐI)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Sáng:</strong></span>&nbsp;Xe và HDV đón Quý khách tại điểm hẹn khởi hành đến nhà ga T2 sân bay Quốc tế Nội Bài, Quý khách làm thủ tục đáp chuyến bay VJ901 (12h15 – 15h50) đi&nbsp;<a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(10, 89, 212); text-decoration-line: none;\"><span style=\"color:rgb(0, 128, 0)\"><strong>du lịch Thái Lan</strong></span></a>.&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Quý khách tự túc ăn trưa (chi phí tự túc)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Đến sân bay quốc tế Suvanabhumi Thái Lan, đoàn làm thủ tục nhập cảnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Tối:&nbsp;</strong></span>Xe đưa đoàn đi ăn tối tại nhà hàng. Tiếp tục khởi hành về&nbsp; Bangkok trên đường đi Quý khách ngắm cảnh thành phố Bangkok lung linh về đêm và tòa nhà Bayjoke Sky cao nhất Thái Lan ( 86 tầng )</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nghỉ đêm tại khách sạn S.D AVENUE BANGKOK hoặc tương đương</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 02: BANGKOK -&nbsp;</strong><strong>WAT TRAIMIT – WAT SAKET – TIGER ZOO – PATTAYA (ĂN SÁNG / TRƯA/TỐI)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Sáng:</strong></span>&nbsp;Quý khách ăn sáng tại khách sạn. Sau đó xe đưa đoàn tham quan, chiêm bái lễ Phật</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>+ Chùa Phật Vàng Wat Traimit</strong></span>&nbsp;– Một trong những ngôi chùa linh thiêng nhất tại đất nước Thái Lan với bức tượng Phật Vàng Golden Buhhdha nằm ở chính điện - &nbsp;bức tượng Phật Vàng lớn nhất thế giới: cao 3 mét và nặng hơn 5,5 tấn. Tượng được đúc theo phong cách Sukhothai tĩnh lặng và được khám phá một cách tình cờ vào thập niên 1950 Người địa phương cho rằng bức tượng lớn nhất thế giới này biểu thị cho sự thịnh vượng và thuần khiết cũng như sức mạnh và quyền năng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>+ Chùa Núi Vàng (Wat Saket - Golden Mount hoặc ‘Phu Khao Thong’)</strong></span>&nbsp;là một ngon đồi thấp với một bảo tháp bằng vàng lấp lánh. Bên trong, tòa tháp 58m có chứa xá lợi Phật và đón chào du khách đến chiêm bái suốt năm. Ngôi chùa cũng tổ chức lễ hằng năm vào tháng 10, diễn ra một tuần trong suốt Loy Krathong (lễ hội thả đèn hoa đăng lên trời).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Trưa:</strong></span>&nbsp;Quý khách ăn Trưa tại nhà hàng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Sau đó khởi hành đi Pattaya, trên đường đi Quý khách tham quan Trại Hổ Siracha Tiger Zoo xem các tiết mục biểu diễn như: xiếc hổ, xiếc voi, xiếc lợn, xiếc cá sấu…</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Tối:</strong></span>&nbsp;Quý khách ăn Tối tại nhà hàng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nghỉ đêm tại khách sạn MANTRA PURA RESORT&nbsp; hoặc tương đương.</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 03: PATTAYA&nbsp; -&nbsp; CORAL ISLAND&nbsp; –&nbsp; WORLD GEM GALLERY –&nbsp; LÂU ĐÀI TỶ PHÚ –&nbsp; KHAO CHEECHAN – COLOSSUEM SHOW&nbsp;&nbsp;(ĂN SÁNG / TRƯA/TỐI)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Sáng:</strong></span>&nbsp;Quý khách ăn sáng tại khách sạn. Sau đó xe đưa đoàn ra biển Pattaya, quý khách lên tàu cao tốc Đảo San Hô (Coral Island). Quý khách có thể tham dự các trò chơi như: dù bay, câu cá, lướt ván, lái Scooter trên mặt biển, thám hiểm dưới đáy biển…(chi phí tự túc). Quý khách tự do tắm biển Pattaya với làn nước trong xanh, bãi cát dài trắng mịn.Trở lại đất liền, Quý khách ăn trưa tại nhà hàng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Chiều:&nbsp;</strong></span>Xe và HDV đưa đoàn thăm quan:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>- Trung tâm chế tác Vàng Bạc Đá Hoàng Gia Quý World Gems Gallery</strong></span>&nbsp;nổi tiếng và lớn nhất của Thái Lan. Nơi đây trưng bày các tác phẩm nghệ thuật được làm từ đá quý của các nghệ nhân người Thái: các bức tượng công, hổ, rồng được làm hoàn toàn bằng vàng và đá quý; các bức tranh khảm đá quý: bát mã, bát tiên quá hải…. Ngoài ra, đây còn là nơi diễn các phiên trao đổi mua bán đá quý đạt chứng chỉ ISO 9002 về quản lý chất lượng, nơi trưng bày các loại đá quý đẹp nổi tiếng, cung cấp những thông tin bổ ích về quá trình khai thác, chế tác đá quý tại Thái Lan và trên thế giới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><span style=\"color:rgb(0, 128, 0)\">- Lâu đài tỷ phú Baan Sukhawadee</span>&nbsp;</strong>- Có vị trí ven biển, kiến trúc chính được lấy ý tưởng từ những hoàng cung, cung điện&nbsp;Versailles, mang đến cái nhìn hấp dẫn về mặt kiến trúc, tòa lâu đài còn chưa đựng những ý nghĩa ẩn về triết lý nhân sinh, tôn giáo sâu sắc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>- Trân Bảo Phật Sơn Khao CheeChan</strong></span>&nbsp;– Ngọn núi có bức tượng vàng khắc bằng Laze trên vách núi, dùng 999kg vàng, cao 130m. Xung quanh được bao phủ bởi vô số loại hoa khác nhau trong những khu vườn được chăm sóc tỉ mỉ. Khu vực được bảo vệ bởi Hải Quân Hoàng Gia&nbsp;Thái Lan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">- Xe đưa đoàn đi ăn tối tại nhà hàng, sau đó đưa đoàn đi thưởng thức&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>chương trình Show Colloseum&nbsp;</strong></span>chỉ có ở Thái Lan - Chương trình nghệ thuật chuyên nghiệp, hiện đại, mang màu sắc của nhiều quốc gia do các diễn viên chuyển đổi giới tính biểu diễn<em>.&nbsp;</em>Đoàn về khách sạn nghỉ ngơi tự do khám phá thành phố về đêm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nghỉ đêm tại khách sạn MANTRA PURA RESORT&nbsp; hoặc tương đương.</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 04: PTY – AQUARIUM PATTAYA – VƯỜN SAITHIP -&nbsp; TT NGHIÊN CỨU RẮN ĐỘC – CỬA HÀNG ĐỒ DA – TRUNG TÂM THƯƠNG MẠI&nbsp;&nbsp;(ĂN SÁNG/TRƯA)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Sáng:</strong></span>&nbsp;Sau khi ăn sáng tại khách sạn, Quý khách trả phòng. Xe đưa đoàn khởi hành về Bangkok. Trên đường đi ghé thăm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>- Công viên thủy cung Aquarium Pattaya</strong></span>&nbsp;- Nằm cách trung tâm thành phố chỉ 6km, Thủy cung Underwater World Pattaya là ốc đảo trong xanh tập trung vô số loài sinh vật biển. Đến đây, du khách có cơ hội hòa mình vào thế giới đại dương bao la và cảm nhận hơi thở biển cả với không gian mở 180° đầy ngoạn mục.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>- Vườn Saithip (Saithip Garden)&nbsp;</strong></span>- Là nơi trưng bày sản phẩm 3 miền của vương quốc Thái Lan. Quý khách có thể tự do mua sắm các loại đặc sản như: Huyết Yến đóng hộp, Mật Ong thiên nhiên, cao hổ cốt,…).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>- Trung tâm nghiên cứu Rắn độc Hoàng Gia:</strong></span>&nbsp;Quý khách thưởng thức Show diễn bắt rắn bằng tay không, sau đó Quý khách được nghe các dược sỹ giới thiệu vàgiải thích về công dụng của những loài rắn qúy hiếm trên vương quốc Thailand và các loại thuốc chế biến từ rắn mà người Thái đã nghiên cứu suốt hơn 100 năm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>- Cửa hàng đồ Da(Leather Factory Outlets) nổi tiếng của Thái Lan</strong></span><em>.&nbsp;</em>Quý khách có thể tìm hiểu về cách chế biến và phân loại các sản phẩm từ da động vật, mua sắm tại trung tâm đồ da của Hoàng Gia.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Quý khách ăn trưa tại nhà hàng, sau đó tiếp tục di chuyển về thành phố Bangkok.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Chiều:&nbsp;</strong></span>Xe đưa Quý khách đi mua sắm tại các trung tâm thương mại lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>+ Big C:</strong></span>&nbsp;Trung tâm thương mại 4 tầng nằm trên đường Ratchada, Từ đây, quý khách có thể ghé thăm Đền Phật Bốn Mặt Erawan– Nằm ngay khu trung tâm Erawan Bangkok, là một trong những ngôi đền linh thiêng tại Thái Lan, nơi người dân và du khách bốn phương đến để tham quan và dâng hương cầu may mắn, hạnh phúc, làm ăn phát tài.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>+ Central World:</strong></span>&nbsp;Là trung tâm mua sắm lớn nhất Thái Lan, đồng thời là một trong số những trung tâm mua sắm lớn nhất thế giới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>+ Platunam Market:</strong></span>&nbsp;Là khu chợ nằm ở ngã tư Ratchaprop và Petchburi, chuyên cung cấp các mặt hàng như quần áo, giày dép, phụ kiện thời trang với giá sỉ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Tối:</strong></span>&nbsp;Quý khách tự túc chi phí ăn tối coupon tại siêu thị.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nghỉ đêm tại khách sạn S.D AVENUE BANGKOK hoặc tương đương.</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 05: WATYANAWWA – HÀ NỘI (ĂN SÁNG, TRƯA )</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Sáng</strong></span>: Ăn sáng tại khách sạn, trả phòng. Xe đưa Quý khách đi tham quan<em>:</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">+&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Chùa Thuyền Watyanawa</strong></span>&nbsp;hay còn gọi là Chùa Xá lợi – Một trong những ngôi chùa linh thiêng nhất của Thái Lan với hình dáng chùa được xây dựng theo kiểu kiến trúc hình dáng con thuyền cùng với kiến trúc Thái là các mái cao vút mang đậm phong cách thời Ayuthaya nằm ngay cạnh dòng sông mẹ Chaophraya. Nơi đây được biết tới là ngôi chùa còn lưu giữ nhiều xá lị nhất của các bậc cao tăng tu thành đắc đạo. Điều các Phật tử tới đây thỏa lòng nhất có lẽ là xin tịnh về một viên xá lị để cầu bình an và thể hiện long thành kính của mình.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">+ Xe tiếp tục đưa Quý khách ra bến tàu lên tàu&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>du thuyền trên dòng sông Chaophraya</strong></span>&nbsp;- dòng sông mẹ của đất nước Thái Lan. Du khách nhìn ngắm phong cảnh hai bên bờ và &nbsp;hiện tượng cá nổi trên dòng sông lớn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>Trưa:</strong></span>&nbsp;Quý khách ăn Trưa coupon tại sân bay</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Quý khách thủ tục đáp chuyến bay VJ902 (15h50 – 17h50) về Hà Nội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">17h50: Đến sân bay Nội Bài, xe đưa đoàn về điểm đón khách ban đầu, chia tay và hẹn gặp lại Quý Khách trên những lộ trình mới!</span></p>', 5, 6990000, 1, 18, 1, NULL, 2),
(8, 'VIP10S17', 'Du lịch Thái Lan 5 ngày 4 đêm Từ thành phố Hồ Chí Minh tour đặc sắc', 'images/du-lich-thai-lan1 (2).jpg', 'du-lich-thai-lan-5-ngay-4-dem-tu-thanh-pho-ho-chi-minh-tour-dac-sac', 'Hàng ngày', 1, 1, '<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong>NGÀY 01: TP. HỒ CHÍ MINH - THÁI LAN</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Quý khách tập trung tại: cổng D2, cột số 12, lầu 02 – Ga đi quốc tế&nbsp;<strong>Tân Sơn Nhất</strong>. Trưởng đoàn Việt Nam làm thủ tục.&nbsp;Chuyến bay BL661đi Bangkok lúc 12:10. Đến&nbsp;<strong>sân bay quốc tế Suvarnabhumi</strong>&nbsp;- sân bay lớn và hiện đại nhất Đông Nam Á.</p>\r\n\r\n<p style=\"text-align:justify\">HDV đón đoàn, tham quan thành phố Bangkok: Dùng bữa tối với món lẩu&nbsp;<strong>Suki Yaki</strong>&nbsp;tự chọn. Về khách sạn, nhận phòng, nghỉ đêm tại Bangkok.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20thai%20lan/baiyoke-sky-hotel-4-1.jpg\" style=\"border:0px; box-sizing:border-box; height:575px; max-width:100%; vertical-align:middle; width:840px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong>NGÀY 02: BANGKOK - TRẠI HỔ SRIRACHA PATTAYA</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dùng bữa sáng tại khách sạn, trả phòng. Sau đó tham quan thành phố<strong>&nbsp;</strong>Bangkok: Chùa&nbsp;<strong>Wat saket&nbsp;</strong>một trong những ngôi chùa nổi tiếng của<strong>&nbsp;Bangkok.&nbsp;</strong>Để lên thăm ngôi chùa, du khách phải leo 318 bậc thang hình dạng xoắn lên đỉnh núi. Trên đỉnh núi, du khách có thể ngắm toàn cảnh Bangkok.</p>\r\n\r\n<p style=\"text-align:justify\">Dùng cơm trưa, khởi hành về Pattaya. Tham quan trại hổ&nbsp;<strong>Sriracha TigerZoo:</strong>&nbsp;Xiếc cá sấu, Xiếc hổ. Heo chạy đua, heo làm toán. Khu vực nuôi hổ, cá sấu, hổ nuôi heo, heo nuôi hổ. Quý khách có thể chụp hình chung với chúa tể sơn lâm to lớn.</p>\r\n\r\n<p style=\"text-align:justify\">Đến thành phố&nbsp;<strong>Pattaya</strong>, nhận phòng, dùng bữa tối.&nbsp;<strong>Trải nghiệm Massage cổ truyền Thailan</strong>&nbsp;giúp lưu thông khí huyết, hồi phục sức khoẻ. Sau đó tự do khám phá thành phố biển về đêm, nghỉ đêm tại&nbsp;<strong>Pattaya</strong>.</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(10, 89, 212); text-decoration-line: none;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20thai%20lan/tour-du-lich-thailand-6n5d-massage-thai-co-truyen-tri-gia-500b-lau-suki-nhat-bbq-hai-san-2014122413442949.jpg\" style=\"border:0px; box-sizing:border-box; height:560px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong>NGÀY 03: PATTAYA - NHÀ TỶ PHÚ - ALCAZAR</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Điểm tâm tại khách sạn, ra đảo san hô bằng tàu: Đảo san hô nằm trong vịnh Thailan có bờ cát trắng, làn nước trong xanh. Quý khách tự túc tắm biển hoặc tham gia các trò chơi ngoài đảo như:&nbsp;<strong>Lặn biển, Mô tô nước, dù kéo, trái chuối, dù kéo</strong>…</p>\r\n\r\n<p style=\"text-align:justify\">Trở lại đất liền, dùng cơm trưa, nghỉ ngơi.</p>\r\n\r\n<p style=\"text-align:justify\">Sau đó đến Trung tâm World Gem nơi trưng bày các loại trang sức bằng đá quý.&nbsp;<strong>Trân Bảo Phật Sơn&nbsp;</strong>tượng Phật khổng lồ bằng vàng.&nbsp;<strong>Vườn nho</strong>&nbsp;khung cảnh làng quê Châu Âu.</p>\r\n\r\n<p style=\"text-align:justify\">Tiếp tục tham quan<strong>&nbsp;Baansukhawadee:&nbsp;</strong>được mệnh danh là ngôi nhà hạnh phúc &nbsp;được xây dựng tráng lệ, nguy nga bên bờ biển, mô phỏng theo cung điện Versailles. Đây là điểm tham quan mới và hấp dẫn tại Pattaya. Lâu đài là một quần thể kiến trúc hài hòa, độc đáo và đem lại những triết lý nhân sinh, tâm linh sâu sắc.</p>\r\n\r\n<p style=\"text-align:justify\">Dùng cơm tối tại nhà hàng địa phương. Xem chương trình đại nhạc hội nổi tiếng Thailan:&nbsp;<strong>Alcazar show</strong>. Xe đưa quý khách về khách sạn, tự do khám phá thành phố về đêm, nghỉ đêm tại Pattaya.</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(10, 89, 212); text-decoration-line: none;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20thai%20lan/thai-lan6.jpg\" style=\"border:0px; box-sizing:border-box; height:473px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong>NGÀY 04:&nbsp;PATTAYA - BANGKOK&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dùng điểm tâm tại khách sạn, trả phòng, khởi hành về Bangkok, tham quan:&nbsp;<strong>Vườn bướm – Butterfly garden</strong>: tìm hiểu về công việc của những người làm nghề thu hoạch tổ Yến.&nbsp;<strong>Trại rắn</strong>: biểu diễn với các loài rắn, tìm hiểu về y học cổ truyền của người Thái.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Trung tâm đồ da</strong>&nbsp;nổi tiếng của Thailan. Dùng bữa trưa tại nhà hàng xoay&nbsp;<strong>buffet quốc tế Baiyoke sky&nbsp;</strong>ngắm toàn cảnh thành phố Bangkok từ trên cao. Dùng bữa trưa &nbsp;với hơn 100 món ăn khắp thế giới. Đặc biệt đối với những du khách ăn chay, phần ăn chay được chuẩn bị khá chu đáo và tinh tế.</p>\r\n\r\n<p style=\"text-align:justify\">Buổi chiều&nbsp;<strong>viếng Phật Bốn Mặt.</strong>&nbsp;Tự do tham quan mua sắm, dùng bữa tối tại các siêu thị như&nbsp;<strong>BigC, Central World, Siam Paragon hay chợ sỉ Platinum, Pratunam…&nbsp;</strong>Đến giờ hẹn xe và HDv đón đoàn về khách sạn, nghỉ đêm tại Bangkok.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20thai%20lan/bangkok-pattaya-tang-buffet-baiyoke-sky-86-tang-alcaza-show-tour-le-30-04-201532155922973.jpg\" style=\"border:0px; box-sizing:border-box; height:560px; max-width:100%; vertical-align:middle; width:840px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong>NGÀY 05: BABGKOK - CHÙA PHẬN VÀNG - TP. HỒ CHÍ MINH</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dùng điểm tâm, trả phòng, tham quan:&nbsp;<strong>Chùa Phật Vàng Wat Trimit</strong>: ngôi chùa linh thiêng nằm ngay trung tâm Yaowarat. Chiêm ngưỡng<strong>&nbsp;Tượng Phật Vàng nặng 5,500kg vàng</strong>: bức tượng Phật khổng lồ bằng vàng nguyên khối chứa đựng trong đó là cả một câu chuyện riêng&nbsp; kỳ thú.&nbsp;<strong>Dạo thuyền trên sông Chaophaya</strong>&nbsp;huyền thoại, ngắm một Bangkok khác lạ từ dưới sông.&nbsp;<strong>Xem hiện tượng cá nổi trên sông,</strong>&nbsp;đặc biệt là khu vực gần các ngôi chùa. Quý khách có thể cho cá ăn bánh mì sẽ thấy hang tram ngàn con cá nổi lên chen kín mặt song. Đoàn khởi hành ra sân bay.Dùng cơm trưa Coupon tại khu ẩm thực.</p>\r\n\r\n<p style=\"text-align:justify\">Đáp chuyến bay&nbsp;<strong>BL662</strong>&nbsp;lúc&nbsp;<strong>15h25&nbsp;</strong>&nbsp;về Việt Nam, đến sân bay Tân Sơn Nhất: Nhập cảnh, nhận hành lý, chia tay trưởng đoàn, kết thúc chuyến tham quan.</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://www.vietnamtravel.net.vn/\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(35, 82, 124); text-decoration-line: none; outline: 0px;\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/du%20lich%20thai%20lan/wat-benjamaborphit-90427924.jpg\" style=\"border:0px; box-sizing:border-box; height:560px; max-width:100%; vertical-align:middle; width:840px\" /></a></p>', 5, 6990000, 1, 10, 1, NULL, 1),
(9, 'HN-HL3', 'Du lịch Hạ Long khám phá kỳ quan thiên nhiên thế giới mới', 'images/Du-Thuyen-Starlight-Ha-Long.jpg', 'du-lich-ha-long-kham-pha-ky-quan-thien-nhien-the-gioi-moi', 'Hàng ngày', 2, 12, '<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(255, 0, 0)\"><strong>NGÀY 01: HÀ NỘI - HẠ LONG - SỬNG SỐT - TITOP&nbsp;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">07h45 - 08h30: Xe và hướng dẫn viên đón Quý khách tại khách sạn trong khu vực Phố Cổ - quận Hoàn Kiếm &amp; Nhà Hát Lớn, khởi hành đi Hạ Long. Trên đường dừng chân nghỉ 20 phút</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">12h30: Tới cảng tàu du lịch Hạ Long, thuyền nhỏ đưa Quý khách ra du thuyền Hạ Long và được thủy thủ đoàn chào đón bằng khăn lạnh cùng ly nước mát</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">13h00:&nbsp;Thưởng thức bữa trưa trên du thuyền, chiêm ngưỡng Vịnh Hạ Long - Di Sản Thiên Nhiên Thế Giới 2 lần được Unessco công nhận</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">14h30: Tham quan hang Sửng Sốt - một trong những hang động rộng và đẹp nhất của vịnh Hạ Long với muôn vàn nhũ đá mang nhiều hình thù kỳ ảo và huyền bí.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">15h30:&nbsp;Tàu cập đảo TiTốp, tắm biển hoặc lên đỉnh núi chiêm ngưỡng toàn cảnh vịnh Hạ Long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">17h30:&nbsp;Quý khách thưởng thức tiệc Sunset Party với rượu vang và hoa quản trái cây, ngắm hoàng hôn trên boong tàu sau đó tham gia chương trình cooking class.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">19h00:&nbsp;Thưởng thức bữa tối trên du thuyền với không gian lãng mạn trên biển</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">20h00:&nbsp;Tham gia các hoạt động vui chơi giải trí trên du thuyền như: Bi lắc, phi tiêu, câu mực đêm, hát karaoke,… nghỉ đêm trên du thuyền Golden Star Cruise</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(255, 0, 0)\"><strong>NGÀY 02: HẠ LONG - NGỌC TRAI - KAYAK - HẠ LONG&nbsp;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">06:30 - 07:30: Quý khách có thể dậy sớm ngắm bình minh và thưởng thức bữa sáng cùng với trà và cafe phục vụ miễn phí</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">08h00:&nbsp;Tham quan làng Ngọc Trai, tìm hiểu những quy trình sản xuất, nuôi trai lấy ngọc. Sau đó tham gia hoạt động chèo kayak khám phá các hang động nhỏ và những vùng nước xanh màu ngọc bích của vịnh Hạ Long</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">10h00:&nbsp;Quay trở lại tàu để trả phòng, tự do thư giãn, tắm nắng, chiêm ngưỡng hòn Gà Chọi - biểu tượng của Vịnh Hạ Long và các đảo đá vôi trong khi du thuyền quay trở lại bến cảng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">11h30:&nbsp;Quý khách thưởng thức bữa trưa trên du thuyền trong trong tàu di chuyển vào bến cảng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">12h30:&nbsp;Xe đưa Quý khách về khách sạn khu vực Tuần Châu hoặc Hạ Long nhận phòng nghỉ ngơi</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Chiều:&nbsp;Quý khách tự do nghỉ ngơi tắm biển hoặc tham gia các hoạt động vui chơi giải trí</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối:Sau bữa tối tại khách sạn, Quý khách dành thời gian tự do nghỉ ngơi dạo chơi khám phá đảo Tuần Châu hoặc TP Hạ Long về đêm. Nghỉ đêm tại khách sạn</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(255, 0, 0)\"><strong>NGÀY 03: HẠ LONG - HÀ NỘI&nbsp;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Sáng:&nbsp;Sau bữa sáng. Quý khách tự do dạo chơi tắm biển, khám phá TP. Hạ Long hoặc đặt mua vé vào khu vui chơi giải trí Dragon Park Hạ Long&nbsp;<em>(chi phí vé vui chơi giải trí không bao gồm)</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">11h00:&nbsp;Làm thủ tục trả phòng sau đó dùng bữa trưa tại nhà hàng hoặc khách sạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">13h00:&nbsp;Xe đón Quý khách trở về&nbsp;Hà Nội, trên đường dừng chân nghỉ ngơi 20 phút, tại đây Quý khách có thể thăm quan mua sắm đồ lưu niệm hoặc đặc sản địa phương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">16h30 - 17h00: Về tới khách sạn khu vực Phố Cổ, chia tay Quý khách, kết thúc chương trình du lịch Hạ Long 3 ngày</span></p>', 3, 2999000, 1, 10, 0, 10, 0),
(10, 'HN-HL1', 'Du lịch Hạ Long – Cát Bà – Đảo Khỉ 3 ngày 2 đêm hè Khởi hành từ Hà Nội hè năm 2021', 'images/ha-long1.png', 'du-lich-ha-long-cat-ba-dao-khi-3-ngay-2-dem-he-khoi-hanh-tu-ha-noi-he-nam-2021', 'Hàng ngày', 1, 12, '<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong><span style=\"font-family:tahoma,geneva,sans-serif\">NGÀY 1: HÀ NỘI – HẠ LONG – SỬNG SỐT – TITOP</span></strong></span></p>\r\n\r\n<div style=\"box-sizing: border-box; color: rgb(0, 0, 0); font-family: \"><span style=\"font-family:tahoma,geneva,sans-serif\">07h45 – 08h30:&nbsp;Xe và hướng dẫn viên đón Quý khách tại khách sạn khởi hành đi Hạ Long. Trên đường dừng chân nghỉ 20 phút</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">12h30:&nbsp;Tới cảng tàu du lịch Hạ Long, thuyền nhỏ đưa Quý khách ra du thuyền Hạ Long và được thủy thủ đoàn chào đón bằng khăn lạnh cùng ly nước mát</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">13h00:&nbsp;Thưởng thức bữa trưa trên du thuyền, chiêm ngưỡng Vịnh Hạ Long – Di Sản Thiên Nhiên Thế Giới 2 lần được Unessco công nhận</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">14h30:&nbsp;Tham quan hang Sửng Sốt – một trong những hang động rộng và đẹp nhất của vịnh Hạ Long với muôn vàn nhũ đá mang nhiều hình thù kỳ ảo và huyền bí.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">15h30:&nbsp;Tàu cập đảo TiTốp, tắm biển hoặc lên đỉnh núi chiêm ngưỡng toàn cảnh vịnh Hạ Long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">17h30: Quý khách thưởng thức tiệc Sunset Party với rượu vang và hoa quản trái cây, ngắm hoàng hôn trên boong tàu sau đó tham gia chương trình cooking class.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">19h00:&nbsp;Thưởng thức bữa tối trên du thuyền với không gian lãng mạn trên biển</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">20h00: Tham gia các hoạt động vui chơi giải trí trên du thuyền như: Bi lắc, phi tiêu, câu mực đêm, hát karaoke,… nghỉ đêm trên du thuyền Golden Star Cruise</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong><span style=\"font-family:tahoma,geneva,sans-serif\">NGÀY 2: HẠ LONG – LAN HẠ – ĐẢO KHỈ – CÁT BÀ</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">06:30 – 07:30:&nbsp;Quý khách có thể dậy sớm ngắm bình minh và thưởng thức bữa sáng cùng với trà và cafe phục vụ miễn phí</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">08h00:&nbsp;Tham quan làng Ngọc Trai, tìm hiểu những quy trình sản xuất, nuôi trai lấy ngọc. Sau đó tham gia hoạt động chèo kayak khám phá các hang động nhỏ và những vùng nước xanh màu ngọc bích của vịnh Hạ Long &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">09:30:&nbsp;Quý khách quay trở lại tàu trả phòng và sang tàu chuyển tải đi thăm vịnh Lan Hạ – Việt Hải</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">10h30:&nbsp; Tàu cập vào Đảo Khỉ, tại đây Quý khách tự do nghỉ ngơi thăm quan tắm biển. Tiếp theo, tàu cập bến Việt Hải, Quý khách đi xe đạp qua đường mòn xuyên cánh rừng để đến thăm làng Việt Hải và thăm vườn quốc gia Cát Bà. Sau đó quay trở về lại bến và lên tàu dùng bữa trưa trên vịnh Lan Hạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">16h00:&nbsp;Tàu cập cảng Bến Bèo, xe đón Quý khách vào thị trấn Cát Bà, nhận phòng nghỉ ngơi</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">19h00:&nbsp;Dùng bữa tối, sau đó là khoảng thời gian tự do khám phá vẻ đẹp của Cát Bà về đêm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong><span style=\"font-family:tahoma,geneva,sans-serif\">NGÀY 3: CÁT BÀ – HẠ LONG – HÀ NỘI</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sáng:&nbsp;Quý khách có thể dậy sớm tắm biển, ngắm bình minh chào đón ngày mới trên đảo Cát Bà</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">07h00:&nbsp;Sau bữa sáng tại khách sạn, Quý khách làm thủ tục trả phòng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">07h30:&nbsp;Xe và hướng dẫn viên đón Quý khách trở lại bến tàu để quay trở về Hạ Long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">11h30:&nbsp;Quý khách dùng bữa trưa trên tàu, trong khi tàu di chuyển về bến Tuần Châu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">12h30:&nbsp;Xe đón quý khách trở về&nbsp;Hà Nội, dừng chân nghỉ ngơi tại Hải Dương 20 phút</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">16h30:&nbsp;Về đến điểm hẹn ban đầu, hướng dẫn viên chia tay Quý khách kết thúc chương trình</span></p>', 3, 3500000, 1, 20, 0, NULL, 0),
(11, 'HCM-HL44', 'Du lịch Hạ Long – Cát Bà – Đảo Khỉ 3 ngày 2 đêm hè Khởi hành từ Hà Nội hè', 'images/sun-legend-Ha-Long (6).jpg', 'du-lich-ha-long-cat-ba-dao-khi-3-ngay-2-dem-he-khoi-hanh-tu-ha-noi-he', 'Hàng ngày', 1, 12, '<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>NGÀY 1: TP HỒ CHÍ MINH&nbsp;– THỦ ĐÔ HÀ NỘI</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sáng:&nbsp;Quý khách tập trung tại sân bay&nbsp;Tân Sơn Nhất&nbsp;làm thủ tục đáp chuyến bay ra&nbsp;Hà Nội.&nbsp;Tới sân bay&nbsp;Nội Bài,&nbsp;xe đưa Quý khách khởi hành tham quan Hà Nội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Đến Hà nội quý khách tham quan&nbsp;Chùa Trấn Quốc&nbsp;– một trong những ngôi chùa cổ kính và linh thiêng nằm bên bán đảo Tây Hồ. Quý khách có dịp tham quan &amp; chụp hình trên&nbsp;đường Thanh Niên&nbsp;– con đường được mệnh danh là&nbsp;đẹp, lãng mạn &amp; độc đáo nhất Hà Nội&nbsp;nằm giữa hồ Tây, hồ Trúc Bạch. Quý khách tự do thưởng thức&nbsp;đặc sản bánh Tôm hồ Tây&nbsp;nổi tiếng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Đoàn vào thăm&nbsp;Lăng Chủ Tịch Hồ Chí Minh– Người lãnh đạo vĩ đại của&nbsp;dân tộc Việt Nam.&nbsp;Quý khách thăm khu quần thể&nbsp;Lăng Bác:&nbsp;thăm nhà sàn Bác Hồ, ao cá Bác Hồ, thăm bảo tàng Hồ Chí Minh…</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Điểm tham quan tiếp theo là&nbsp;Chùa Một Cột&nbsp;– ngôi chùa cổ kính linh thiêng có kiến trúc độc đáo mang dáng dấp của một đài sen mọc lên giữa lòng hồ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Quý khách tiếp tục lên xe đi thăm quan&nbsp;Bảo tàng Dân tộc học,&nbsp;nơi lưu giữ và trưng bày nhiều hiện vật văn hoá của cả 54 dân tộc Việt Nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sau đó đoàn đưa quý khách trở về nhà hàng trên phố cổ để ăn trưa</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Chiều:&nbsp;Quý khách đi tham quan&nbsp;Văn Miếu Quốc Tử Giám - một trong những công trình kiến trúc cổ, xây dựng tương đối quy mô còn lại được đến ngày nay ở&nbsp;thủ đô Hà Nội.&nbsp;Văn Miếu&nbsp;được xây dựng tháng 10 năm 1070,&nbsp;thờ Khổng Tử, các bậc Hiền triết củ a&nbsp;Nho giáo&nbsp;và&nbsp;Tư nghiệp Quốc Tử Giám&nbsp;Chu Văn An, người thầy tiêu biểu đạo cao, đức trọng của nền giáo dục Việt Nam. Năm 1076,&nbsp;nhà&nbsp;Quốc Tử Giám&nbsp;được xây kề sau&nbsp;Văn Miếu, ban đầu là nơi học của các&nbsp;Hoàng Tử, sau mở rộng thu nhận cả các học trò giỏi trong thiên hạ. Cho đến ngày nay, trải qua nhiều biến cố, nhưng&nbsp;Văn miếu – Quốc Tử Giám&nbsp;mãi mãi được coi như một nơi trang nghiêm, nơi đề cao sự học của người&nbsp;Việt Nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Cuối cùng xe đưa quý khách thăm quan&nbsp;Hồ Hoàn Kiếm– trái tim của&nbsp;thủ đô Hà Nội. Quý khách tự do tham quan, chụp ảnh&nbsp;Cầu Thê Húc, Đền Ngọc Sơn&nbsp;– một trong những địa điểm không thể bỏ qua khi tới Hà Nội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Quý khách nhận phòng khách sạn trong khu vực&nbsp;Phố Cổ Hà Nội, nghỉ ngơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Quý khách tự do dạo chơi, thưởng thức bữa tối với ẩm thực Hà Nội, khám phá nét đẹp của&nbsp;36 phố phường Thủ Đô.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/ha-noi-ha-long-sapa/ha-noi-hoa-dep.jpg\" style=\"border:0px; box-sizing:border-box; height:733px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>NGÀY 02: HÀ NỘI – HẠ LONG – SỬNG SỐT – TITOP</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">07h45 – 08h30:&nbsp;Xe và hướng dẫn viên đón Quý khách tại khách sạn khởi hành đi Hạ Long. Trên đường dừng chân nghỉ 20 phút</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">12h30: Tới cảng tàu du lịch Hạ Long, thuyền nhỏ đưa Quý khách ra du thuyền Hạ Long và được thủy thủ đoàn chào đón bằng khăn lạnh cùng ly nước mát</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">13h00: Thưởng thức bữa trưa trên du thuyền, chiêm ngưỡng Vịnh Hạ Long – Di Sản Thiên Nhiên Thế Giới 2 lần được Unessco công nhận</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">14h30:&nbsp;Tham quan hang Sửng Sốt – một trong những hang động rộng và đẹp nhất của vịnh Hạ Long với muôn vàn nhũ đá mang nhiều hình thù kỳ ảo và huyền bí.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">15h30:&nbsp;Tàu cập đảo TiTốp, tắm biển hoặc lên đỉnh núi chiêm ngưỡng toàn cảnh vịnh Hạ Long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">17h30:&nbsp;Quý khách thưởng thức tiệc Sunset Party với rượu vang và hoa quản trái cây, ngắm hoàng hôn trên boong tàu sau đó tham gia chương trình cooking class.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">19h00:&nbsp;Thưởng thức bữa tối trên du thuyền với không gian lãng mạn trên biển</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">20h00:&nbsp;Tham gia các hoạt động vui chơi giải trí trên du thuyền như: Bi lắc, phi tiêu, câu mực đêm, hát karaoke,… nghỉ đêm trên du thuyền Golden Star Cruise.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/ha-noi-ha-long-sapa/ha-long1.png\" style=\"border:0px; box-sizing:border-box; height:708px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>NGÀY 03: HẠ LONG – LAN HẠ – ĐẢO KHỈ – CÁT BÀ</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">06:30 – 07:30:&nbsp;Quý khách có thể dậy sớm ngắm bình minh và thưởng thức bữa sáng cùng với trà và cafe phục vụ miễn phí</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">08h00:&nbsp;Tham quan làng Ngọc Trai, tìm hiểu những quy trình sản xuất, nuôi trai lấy ngọc. Sau đó tham gia hoạt động chèo kayak khám phá các hang động nhỏ và những vùng nước xanh màu ngọc bích của vịnh Hạ Long &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">09:30:&nbsp;Quý khách quay trở lại tàu trả phòng và sang tàu chuyển tải đi thăm vịnh Lan Hạ – Việt Hải</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">10h30:&nbsp; Tàu cập vào Đảo Khỉ, tại đây Quý khách tự do nghỉ ngơi thăm quan tắm biển. Tiếp theo, tàu cập bến Việt Hải, Quý khách đi xe đạp qua đường mòn xuyên cánh rừng để đến thăm làng Việt Hải và thăm vườn quốc gia Cát Bà. Sau đó quay trở về lại bến và lên tàu dùng bữa trưa trên vịnh Lan Hạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">16h00:&nbsp;Tàu cập cảng Bến Bèo, xe đón Quý khách vào thị trấn Cát Bà, nhận phòng nghỉ ngơi</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">19h00:&nbsp;Dùng bữa tối, sau đó là khoảng thời gian tự do khám phá vẻ đẹp của Cát Bà về đêm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/ha-noi-ha-long-sapa/sun-legend-Ha-Long%20(6).jpg\" style=\"border:0px; box-sizing:border-box; height:733px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><strong>NGÀY 04: CÁT BÀ – HẠ LONG – HÀ NỘI</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">Sáng:&nbsp;Quý khách có thể dậy sớm tắm biển, ngắm bình minh chào đón ngày mới trên đảo Cát Bà</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">07h00:&nbsp;Sau bữa sáng tại khách sạn, Quý khách làm thủ tục trả phòng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">07h30:&nbsp;Xe và hướng dẫn viên đón Quý khách trở lại bến tàu để quay trở về Hạ Long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">11h30:&nbsp;Quý khách dùng bữa trưa trên tàu, trong khi tàu di chuyển về bến Tuần Châu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">12h30:&nbsp;Xe đón quý khách trở về&nbsp;Hà Nội, dừng chân nghỉ ngơi tại Hải Dương 20 phút</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\">16h30:&nbsp;Về đến điểm hẹn ban đầu, hướng dẫn viên chia tay Quý khách kết thúc chương trình</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:tahoma,geneva,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/ha-noi-ha-long-sapa/ha-long.png\" style=\"border:0px; box-sizing:border-box; height:580px; max-width:100%; vertical-align:middle; width:1100px\" /></span></p>', 4, 4500000, 1, 18, 1, NULL, 0);
INSERT INTO `tour_trongnuoc` (`id`, `code`, `title`, `image`, `slug_tour`, `date_start`, `start`, `country_id`, `description`, `time`, `price`, `status`, `amount`, `hot`, `giamgia`, `count`) VALUES
(12, 'DN-3Nn', 'Sơn Trà – Bà Nà – Ngũ Hành Sơn – Hội An – Cù Lao Chàm 3 ngày (đã bao gồm vé Máy bay )', 'images/cau-ban-tay-da-nang.jpg', 'son-tra-ba-na-ngu-hanh-son-hoi-an-cu-lao-cham-3-ngay-da-bao-gom-ve-may-bay', 'Thứ 5, Thứ 7 Hàng Tuần', 1, 9, '<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 01: ĐÓN KHÁCH – NGŨ HÀNH SƠN – HỘI AN (ĂN TRƯA, TỐI)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Sáng: Quý khách có mặt tại sân bay, nhân viên đón và làm thủ tục đáp chuyến bay đến Đà Nẵng, xe đón quý khách tại Đà Nẵng, đưa quý khách đến tập trung tại điểm hẹn, check in&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cà Phê Không gian Xưa/Việt Hương/Phố xưa</strong></span>, tự do thưởng thức cà phê, ẩm thực sáng.&nbsp; Tham quan một vòng Đà Nẵng với&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cầu Tình Yêu, tượng Cá chép Hóa Rồng</strong></span>&nbsp;và chiêm ngưỡng những cây cầu đẹp của Đà Nẵng... Xe đưa đi ăn trưa với đặc sản nổi tiếng Đà&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Nẵng “Bánh tráng thịt heo 2 đầu da &amp; Mỳ Quảng”</strong></span>. Nhận phòng Khách sạn nghỉ ngơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Chiều:&nbsp;Quý khách thăm&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>làng nghề điêu khắc đá Non Nước</strong></span>&nbsp;và chinh phục&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Ngũ Hành Sơn</strong>,</span>&nbsp;khám phá vẻ đẹp của những hang động kỳ bí, viếng những ngôi chùa cổ trăm năm&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">Linh Ứng và Tam Thai</span></strong>. Rời Khu danh thắng Ngũ Hành Sơn, đoàn tiếp tục khởi hành đến Phố cổ Hội An. Tham quan&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Hội Quán, Nhà Cổ, Chùa Cầu</strong></span>. Tự do thưởng thức trà &amp; bánh cookie&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Reaching Out Tea House</strong></span>, không gian đặc biệt do những người khiếm thính phục vụ, thả đèn hoa đăng cầu phúc lộc trên dòng sông Hoài….</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Tối:&nbsp;Đoàn dùng bữa tối đặc sản tại Hội An, &nbsp;bách bộ tham quan mua sắm tại Phố cổ, chiêm ngưỡng một Hội An về đêm lung linh trong sắc đèn … Trải nghiệm không gian&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Công viên văn hóa chủ đề</strong>&nbsp;</span>với show diễn&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Ký ức Hội An</strong></span>&nbsp;tái hiện lại thời kỳ hoàng kim của thương cảng Hội An (chi phí tự túc). Trở lại Đà Nẵng, nghỉ ngơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/thong-tin-du-lich-da-nang9.jpg\" style=\"border:0px; box-sizing:border-box; height:668px; max-width:100%; vertical-align:middle; width:1000px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 02: TẮM BIỂN MỸ KHÊ - CHÙA LINH ỨNG&nbsp;BẢO TÀNG HOÀNG SA- BÀ NÀ (ĂN: SÁNG, TRƯA,&nbsp;CHIỀU)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Sáng:&nbsp;Điểm tâm. Tự do<span style=\"color:rgb(0, 128, 0)\">&nbsp;<strong>tắm biển Mỹ Khê</strong></span>. Sau đó xư đưa quý khách tham quan&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Bảo tàng Hoàng Sa</strong></span>&nbsp;trưng bày những hình ảnh, tư liệu khẳng định chủ quyền biển đảo Việt Nam. Tiếp tục tham quan<span style=\"color:rgb(0, 128, 0)\">&nbsp;<strong>Bán Đảo Sơn Trà</strong>&nbsp;</span>để thưởng ngoạn toàn cảnh Thành phố biển Đà Nẵng từ trên cao, Đoàn tham quan&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Chùa Linh Ứng Sơn Trà</strong></span>, nơi có tượng<span style=\"color:rgb(0, 128, 0)\">&nbsp;<strong>Quán Thế Âm Bồ Tát</strong></span>&nbsp;cao nhất Việt Nam tọa sơn hướng hải, cầu bình an cho gia đình. &nbsp;&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Trưa:&nbsp;Ăn trưa nhà hàng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Chiều:&nbsp;Đoàn đi&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Bà Nà Núi Chúa( tự túc cáp treo)</strong></span>&nbsp;chinh phục Tuyến cáp treo mới nhất tại Bà Nà&nbsp;<span style=\"color:rgb(0, 128, 0)\">(<strong>ga Hội An – Marseille – Louvre</strong>)</span>&nbsp;check in tại&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cầu Bàn Tay vàng</strong></span>&nbsp;nổi tiếng. Thỏa sức vui chơi tại&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Fantashy park</strong></span>, Làng Pháp xa hoa, chiêm ngưỡng Hoàng Hôn trên đỉnh Bà Nà.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Tối:&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Ăn tối buffet</strong></span><strong>&nbsp;</strong>và thưởng thức miễn phí 1 lit bia tươi Đức tại Bà Nà. Tự do khám phá Bà Nà về đêm; 21h00: Về lại Đà Nẵng nghỉ ngơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/cau-ban-tay-da-nang.jpg\" style=\"border:0px; box-sizing:border-box; height:500px; max-width:100%; vertical-align:middle; width:1000px\" /></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(255, 0, 0)\"><strong>NGÀY 03: CÙ LAO CHÀM – TIỄN KHÁCH &nbsp;(ĂN SÁNG, TRƯA)</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Sáng:&nbsp; &nbsp;Điểm tâm, làm thủ tục trả phòng. Khởi hành vào Hội An ngồi tàu cao tốc tại biển&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">Cửa</span>&nbsp;<span style=\"color:rgb(0, 128, 0)\">Đại - Hội An</span></strong><span style=\"color:rgb(0, 128, 0)\">&nbsp;đi&nbsp;<strong>Cù Lao Chàm ( chưa bao gồm vé đi Cù Lao Chàm 550.000d)</strong></span>&nbsp;khu dự trữ sinh quyển thế giới, gồm cụm đảo ngoài khơi biển Đông cách Hội An 12 hải lý. Tham quan&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">chùa Hải Tạng</span>&nbsp;</strong>hơn 400 trăm năm tuổi.&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Âu thuyền</strong>&nbsp;</span>khu vực tranh bão của ngư dân,&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>khu làng chài và khu lăng miếu cổ</strong>.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Trưa: &nbsp; Ăn trưa nhà hàng&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cù Lao Chàm</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Chiều: Tiếp tục thăm&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>khu bảo tồn biển</strong></span>&nbsp;và tham gia các&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>hoạt</strong></span>&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>động lặn biển (Snokelling)&nbsp;</strong></span>khám phá san hô... tắm biển.Về lại đất liền. Trở về Đà Nẵng mua sắm tại siêu thị đặc sản miền trung, tiễn khách ra sân bay Đà Nẵng&nbsp; – Kết thúc chương trình, hẹn gặp lại quý khách trong những hành trình sau!.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/Cham-Island-Tour.jpg\" style=\"border:0px; box-sizing:border-box; height:500px; max-width:100%; vertical-align:middle; width:1000px\" /></span></p>\r\n\r\n<div>&nbsp;</div>', 3, 5000000, 1, 18, 0, NULL, 2),
(13, 'DN-5N', 'Du lịch Đà Nẵng Huế: Sơn Trà - Hội An - Bà Nà - Huế Từ TP. Hồ Chí Minh 4 ngày 3 đêm, có vé máy bay', 'images/linh-ung-tu-da-nang_viet-nam-tourism.jpg', 'du-lich-da-nang-hue-son-tra-hoi-an-ba-na-hue-tu-tp-ho-chi-minh-4-ngay-3-dem-co-ve-may-bay', 'Thứ 4, Thứ 7 Hàng Tuần', 1, 9, '<p style=\"text-align:center\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>TP. HỒ CHÍ MINH&nbsp;- ĐÀ NẴNG - SƠN TRÀ - HỘI AN - BÀ NÀ – CỐ ĐÔ HUẾ</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:rgb(0, 128, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">(04&nbsp;Ngày&nbsp;/&nbsp;03&nbsp;Đêm – Khởi hành thứ 4, 7 hàng tuần)</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 1: TP. HỒ CHÍ MINH - ĐÀ NẴNG – BÁN ĐẢO SƠN TRÀ -&nbsp;SUNWHEEL&nbsp;(ĂN TRƯA, TỐI)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng:&nbsp; Quý khách bay ra Đà Nẵng từ sân bay Tân Sơn Nhất ( Nhận vé máy bay ngay khi đặt Tour) trong các chyến bay buổi sáng. Đón quý khách tại Đà Nẵng, ghép đoàn và bắt đầu chương trình tại Đà Nẵng: quý khách đi ăn trưa đặc sản nổi tiếng&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Đà Nẵng “Bánh tráng thịt heo 2 đầu da &amp; Mỳ Quảng”</strong></span>. Nhận phòng&nbsp; nghỉ ngơi ( Khách sạn 3*)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi chiều: quý khách lên&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Bán Đảo Sơn Trà</strong></span>&nbsp;mục kích phố biển Đà Nẵng trên cao, viếng&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Linh Ứng Tự</strong></span>&nbsp;- nơi có&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>tượng&nbsp;Phật Bà</strong></span>&nbsp;67m cao nhất Việt Nam và&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>tắm biển&nbsp;Mỹ Khê.</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối:&nbsp; Ăn tối nhà hang, &nbsp;sau đó trải nghiệm cảm giác với Vòng quay Mặt trời&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>SUN WHEEL</strong>&nbsp;–&nbsp;<strong>Top 10 vòng quay cao nhất Thế Giới</strong>,</span>&nbsp;chiêm ngưởng vẻ đẹp Đà Nẵng&nbsp;về đêm rực rỡ ánh đèn<span style=\"color:rgb(0, 128, 0)\">&nbsp;(<strong>Chi phí vé Sun Wheel tự túc</strong>)</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 128, 0)\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/du-lich-da-nang-gia-re.jpg\" style=\"border:0px; box-sizing:border-box; height:500px; max-width:100%; vertical-align:middle; width:1000px\" /></span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>NGÀY 2:&nbsp;BẢO TÀNG PHẬT HỌC – PHỐ CỔ HỘI AN (ĂN&nbsp;SÁNG, TRƯA, TỐI)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng:&nbsp; Sau khi dung điểm tâm sang tại khách sạn, &nbsp;xe đưa quý khách vòng qua&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cầu Rồng</strong></span>&nbsp;lưu lại dấu ấn trên&nbsp;<span style=\"color:rgb(0, 100, 0)\"><strong>Cầu Tình Yêu</strong></span>, tản bộ thưởng thức không khí trong lành bên&nbsp; bờ&nbsp; Hàn Giang với tượng&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cá Chép Hóa Long</strong>&nbsp;</span>- Biểu tượng mong muốn vươn lên của người Đà Nẵng. Tham quan&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Chùa Quan Thế Âm&nbsp;</strong>-&nbsp;<strong>Bảo Tàng Phật Học</strong>&nbsp;và&nbsp;<strong>Làng Nghề Điêu Khắc</strong>&nbsp;<strong>Đá</strong>.&nbsp;</span>Mua sắm đặc sản&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Qùa Miền Trung</strong>.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi trưa: Ăn trưa nhà hàng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi chiều: Quý khách khởi hành vào&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Hội An</strong></span>&nbsp;bách bộ tham quan và mua sắm&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Phố Cổ</strong>&nbsp;</span>với:&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Chùa Cầu Nhật Bản</strong></span>, Nhà Cổ hàng trăm năm tuổi,&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Hội Quán Phước Kiến</strong></span>&nbsp;&amp; Xưởng thủ công mỹ nghệ. Khởi hành về lại Đà Nẵng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối:&nbsp; Quý khách ăn tối nhà hàng, sau đó tự do thưởng ngoạn&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>du thuyền trên sông Hàn</strong></span>&nbsp;ngắm cảnh Đà Thành về đêm, chụp ảnh&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Cầu Quay Sông Hàn</strong>,&nbsp;<strong>cầu Rồng</strong></span>&nbsp;phun lửa và nước vào cuối tuần.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">&nbsp;<img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/Du%20lich%20da%20nang%20-%20hoi%20an-%20hue/jqw1421124736_tour-du-lich-da-nang-son-tra-hoi-an-cu-lao-cham-ba-na-4-ngay-3-dem.jpg\" style=\"border:0px; box-sizing:border-box; height:473px; max-width:100%; vertical-align:middle; width:1000px\" /></span></p>', 4, 5700000, 1, 10, 0, NULL, 0),
(14, 'PQ-3N', 'Du lịch Phú Quốc khám phá vườn thú Sapari câu cá và lặn biển 2022 ( Tour đường bộ)', 'images/phu-quoc-2018.jpg', 'du-lich-phu-quoc-kham-pha-vuon-thu-sapari-cau-ca-va-lan-bien-2022-tour-duong-bo', 'Hàng ngày', 1, 10, '<p style=\"text-align:justify\"><strong><span style=\"color:rgb(178, 34, 34)\"><span style=\"font-family:arial,helvetica,sans-serif\">ĐÊM THỨ NHẤT: TP. HỒ CHÍ MINH – PHÚ QUỐC</span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối: Xe và HDV đón khách tại điểm hẹn, khởi hành chương trình du lịch Phú Quốc 3 ngày 3 đêm, Quý khách nghỉ đêm trên xe, sau khi đến Hà Tiên&nbsp;quý khách ăn sáng và nghỉ ngơi tại chỗ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/tau-cao-toc-phu-quoc-express-2.jpg\" style=\"border:0px; box-sizing:border-box; height:504px; max-width:100%; vertical-align:middle; width:840px\" /></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:justify\"><strong><span style=\"color:rgb(178, 34, 34)\"><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 1: KHÁM PHÁ ĐẢO NGỌC – PHÚ QUỐC</span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng:&nbsp;Đoàn dùng điểm tâm sáng, sau đó di chuyển đến bến tàu Hà Tiên. Lên tàu&nbsp;để đến với Đảo Ngọc Phú Quốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi trưa: Đoàn dùng bữa trưa tại nhà hàng với món đặc sản địa phương, sau đó về khách sạn nhận phòng nghỉ ngơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi chiều: Xe và HDV đưa đoàn tham quan&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">Dinh Cậu, Thủy Long Thánh Mẫu</span></strong>&nbsp;(Biểu tượng tính ngưỡng của người dân hải đảo (nơi họ tìm đến khấn nguyện cho mỗi chuyến ra khơi).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/dinh-cau_viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:564px; max-width:100%; vertical-align:middle; width:840px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:rgb(178, 34, 34)\">Quý khách có thể chọn các Option:</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><span style=\"color:rgb(178, 34, 34)\">Option 1:</span></strong>&nbsp;Tham quan&nbsp;<strong><span style=\"color:rgb(0, 100, 0)\">Vườn Thú Mở - Vinpearl Safari Phú Quốc</span></strong>&nbsp;&nbsp;theo mô hình&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">Safari Singapore</span></strong>&nbsp;Công viên Chăm sóc và Bảo tồn Động vật Vinpearl Safari Phú Quốc có tổng diện tích gần 500ha, được xây dựng theo mô hình bán hoang dã, trong đó, các động vật quý hiếm được đảm bảo chăm sóc và bảo tồn trong môi trường thiên nhiên mở. Hiện Công viên đã có khoảng 3.000 cá thể thuộc 150 chủng loài, được sưu tầm, bảo tồn từ các động vật hoang dã quý hiếm địa phương, đồng thời quy tụ từ nhiều vùng địa sinh học đặc trưng trên thế giới như Nam Phi, Châu Âu, Úc, Mỹ...(giá vé tham khảo 600.000vnđ).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/vinpear-land-phu-quoc-gia-re_viet-nam-travel.jpg\" style=\"border:0px; box-sizing:border-box; height:600px; max-width:100%; vertical-align:middle; width:840px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(178, 34, 34)\">Option 2:</span></span></strong><span style=\"font-family:arial,helvetica,sans-serif\">&nbsp;Tham quan&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">Vinpearl Land nơi được ví như một \"Disneyland\"&nbsp;của Hồng Kông</span></strong>&nbsp;với nhiều trò chơi trong nhà và ngoài trời dành cho mọi lứa tuổi như: Tàu lượng siêu tốc, Đu quay vòng xoay, Đĩa quay siêu tốc, Đu quay văng……. Biểu diễn cá heo, Công viên nước, Thủy cung ( nhiều loài sinh vật biển và bò sát quý hiếm ), Hoạt náo, lễ hội đường phố,&nbsp; Rạp chiếu phim 5D, Phố mua sắm và ẩm thực đường phố.&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">Du khách thưởng thức&nbsp;<strong><span style=\"color:rgb(0, 128, 0)\">chương trình nhạc nước hoành tráng nhất Đông Nam Á</span></strong>, biểu diễn cá heo, chương trình nàng tiên cá, lễ hội đường phố, hoạt náo đường phố…(Chi phí tham quan tự túc - giá vé tham khảo 500.000 vnđ. Đã có xe đưa đón du khách từ khách sạn tới tới KDL Vinpearl/ Safari).</span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/vinpearlandphuquoc-3848.jpg\" style=\"border:0px; box-sizing:border-box; height:400px; max-width:100%; vertical-align:middle; width:840px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối: Đoàn dùng cơm tối, thưởng thức hải sản tươi ngon. Sau đó Du khách tự do khám phá Phú Quốc về đêm. Nghỉ đêm tại Phú Quốc.</span></p>\r\n\r\n<table align=\"left\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse:collapse; border-spacing:0px; color:rgb(0, 0, 0); font-family:helvetica neue,helvetica,arial,sans-serif; font-size:14px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 2: KHÁM PHÁ NAM ĐẢO – PHÚ QUỐC</span></strong></span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng: Quý khách dậy sớm ngắm cảnh bình minh, đi dạo và dùng điểm tâm sáng sau</span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">đó khởi hành đi tham quan Nam Đảo:&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Khu nuôi cấy Ngọc Trai</strong></span>&nbsp;- một trong những nét đặc trưng mà du khách không thể bỏ qua khi đến với Phú Quốc. Quý khách được tìm hiểu về quy trình nuôi cấy ngọc trai, được tận mắt chứng kiến quá trình mỗ trai lấy ngọc và có cơ hội sỡ hữu những &nbsp;&nbsp;sản phẩm ngọc trai chính hiệu mang đến sự may mắn và thịnh vượng,&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Khu di tích lịch sử Nhà Tù Phú Quốc</strong></span>&nbsp;(Được mệnh danh là địa ngục trần gian, nơi đã từng trải qua hai thời kỳ thực dân Pháp và đế quốc Mỹ).</span></p>\r\n\r\n			<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/ngoc-trai-phu-quoc.jpg\" style=\"border:0px; box-sizing:border-box; height:552px; max-width:100%; vertical-align:middle; width:840px\" /></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Xe đưa đoàn đến&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>cảng biển Dinh Cậu</strong></span>, Du khách lên tàu và bắt đầu cuộc trải nghiệm hành trình:&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Câu cá biển Đông - lặn ngắm san hô và khám phá đảo hoang Phú Quốc</strong></span>. Đoàn được cung cấp thiết bị câu cá ( dây câu, mồi ) và được trang bị áo phao, kính lặn, ống thở khi tham gia lặn biển ngắm san hô.</span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi trưa: Dùng cơm trưa trên tàu, t</span><span style=\"font-family:arial,helvetica,sans-serif\">àu cập bến quý khách được đưa&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>đến Bãi Sao</strong></span>: một trong những bãi biển với làn nước xanh biếc, bờ cát trắng mịn nổi tiếng tại Phú Quốc. Đến Bãi Sao, quý khách tự do nghỉ ngơi, vui chơi và tắm biển ( phí tắm nước ngọt + võng nằm: chi phí tự túc).&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>Tham quan và viếng Chùa Hộ Quốc&nbsp;</strong></span>– đây cũng là một địa điểm du lịch tham quan tâm linh mới của Phú Quốc, tuy được xây dựng cách đây không lâu nhưng chùa đã thu hút rất nhiều du khách và người dân tại đây đến tham quan lễ Phật và ngắm phong cảnh tuyệt đẹp của chùa.&nbsp; &nbsp;&nbsp;</span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối: Dùng cơm tối. Tự do&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>tham quan chợ đêm Dinh Cậu</strong></span>&nbsp;trải nghiệm văn hóa ẩm thực Phú Quốc, du khách có thể đặt&nbsp;<span style=\"color:rgb(0, 128, 0)\"><strong>tour Câu mực đêm&nbsp;</strong></span>(chi phí tự túc) để tận hưởng trọn vẹn hơn vẻ đẹp Phú Quốc về đêm.&nbsp; Nghỉ đêm tại khách sạn.</span></p>\r\n\r\n			<p style=\"text-align:justify\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/an-gi-o-dau-choi-cho-nao-khi-du-lich-phu-quoc1_1.jpg\" style=\"border:0px; box-sizing:border-box; height:573px; max-width:100%; vertical-align:middle; width:840px\" /></p>\r\n\r\n			<hr />\r\n			<p style=\"text-align:justify\"><span style=\"color:rgb(178, 34, 34)\"><strong><span style=\"font-family:arial,helvetica,sans-serif\">NGÀY 3:&nbsp;PHÚ QUỐC – TP. HỒ CHÍ MINH</span></strong></span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi sáng: Quý khách làm thủ tục trả phòng, dùng điểm tâm,&nbsp;khởi hành ra bến tàu Phú Quốc, chào tạm biệt và chia tay Đảo Ngọc.</span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi trưa: Đến bến tàu Hà Tiên, Đoàn dùng cơm trưa. Tiếp tục khởi hành về TP.HCM. Trên đường về, xe dừng chân tại 1 số điểm để đoàn giải lao và mua sắm sản vật miền&nbsp;Tây Nam Bộ.</span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Buổi tối: Về đến TP. Hồ Chí Minh, Xe đưa Quý khách về điểm đón ban đầu. HDV chia tay, tạm biệt và hẹn gặp lại Quý khách.</span></p>\r\n\r\n			<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"https://vietnamtravel.net.vn/media/images/phu-quoc/du-lich-phu-quoc2.jpg\" style=\"border:0px; box-sizing:border-box; height:470px; max-width:100%; vertical-align:middle; width:840px\" /></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 3, 2790000, 1, 18, 1, 10, 1);
